INSERT INTO `records` (`id`, `id_user_logged`, `id_user`, `id_module`, `id_association`, `title`, `slug`, `subtitle`, `intro`, `text`, `picture`, `main`, `cover`, `active`, `deleted`, `created_at`, `updated_at`) VALUES
(1515, 1, 1, 8, 57, 'Бесплатни прегледи за 150 пензионери во ЗП Битола', 'besplatni-pregledi-za-150-penzioneri-vo-zp-bitola', '', 'Успешно реализиран проектот за бесплатно мерење на притисок во очите и шеќер во крвта, како и давање соодветни консултации на пензионерите од ЗП Битола, согласно склучениот меморандум за соработка на Сојузот на здруженијата на пеннзионери на Македонија и Специјализираната болница ,,Промедика офталмо', '<p>Успешно  реализиран проектот за бесплатно мерење на притисок  во   очите и шеќер во крвта, како и давање соодветни консултации на  пензионерите од ЗП Битола, согласно склучениот   меморандум  за соработка на  Сојузот на здруженијата на пеннзионери на Македонија  и Специјализираната болница ,,Промедика  офталмологија&ldquo;, Скопје. <br>\r\n    Заинтересираноста  беше голема од страна на пензионерите. Оваа бесплатна услуга ја искористија  околу 150 пензионери. <br>\r\n    Пензионерите  беа известени дека  со овај меморандум  кој  важи до 31.01.2024 год. има и други поволности  за пензионерите како на пример супспецијалистички  прегледи, испитување на очното дно, операција на катаракта  и други  прегледи на очите во болницата. Цената за  пензионерите  ќе биде намалена за 50% од  редовната. </p>', 'Bitola besplatni pregledi.jpg', 0, 0, 1, 0, '2023-12-21 11:12:12', '2023-12-21 11:12:12'),
(1516, 1, 1, 8, 57, 'Усвоена Програмата и Финансискиот план за 2024 година на ЗП Центар', 'usvoena-programata-i-finansiskiot-plan-za-2024-godina-na-zp-centar', '', 'На почетокот на декември 2023 година, се одржа четвртата седница на Собранието на ЗП Центар која ја раководеше претседателката на Собранието Љубинка Крстева. На почетокот на седницата членовите на Собранието изразија сочувство до семејствата на починатата Вања Ѓорчевска од Центар и на Панче Жежовски', '<p>На почетокот на декември 2023 година, се одржа четвртата седница на Собранието на ЗП Центар која ја  раководеше претседателката на Собранието Љубинка Крстева.<br>\r\n    На почетокот на седницата членовите на Собранието изразија сочувство до семејствата  на починатата Вања Ѓорчевска од Центар и на Панче Жежовски од Велес, како и на  починатите пензионери од ЗП Центар меѓу двете седници. <br>\r\n    Членовите на Собранието  вниманието  го насочија  кон Програмата за работа на  Собранието и на Извршниот одбор на Здружението за 2024 година. Претседателот на  ЗП Центар Злате Стефановски во врска со тоа нагласи дека Програмата е  подготвена врз основа на искуствата од досегашната работа на  органите и телата на Здружението и за секоја  активност во неа е определен и рок за исполнување.<br>\r\n    Подготовките за регионалните и за Републичката фолклорна ревија како и за  спортските натпревари, посетата на болни и изнемоштени пензионери, грижата за  здравјето на пензионерите од Центар, преку редовно мерење на крвен притисок и  шеќер во крвта и други активности, ќе бидат приоритет на ЗП Центар, како и  соработката во оваа насока со Црвениот крст на општината. И во следната година  ќе се организираат еднодневни и дводневни екскурзии во нашата земја и во земјите  од соседството. Во Програмата се набележани и насоки за поттикнување на  активностите и на осумте ограноци на Здружението, а посебно место има и  доделувањето на еднократна парична помош на пензионерите со ниски пензиски  примања. При донесувањето на Програмата беше нагласено дека таа е отворена и  може да се надополнува со прашања и предлози од интерес на членството преку  целата година.<br>\r\n    Беше донесен и Финансискиот план за приходите и расходите на ЗП Центар за  2024 година. Вкупните планирани приходи изнесуваат 6.172.079, а вкупните  расходи 4.078.000 денари.<br>\r\n    Собранието на четвртата седница ја разгледа и Предлог-одлуката од Извршниот  одбор и за измени и дополнувања на Деловникот за работа на Собранието на ЗП  Центар- Скопје. Имено огранокот на просветните работници повеќе не е составен  дел на Здружението и сите промени се во врска со тоа. Членот  во статутарната комисија што досега беше од огранокот  на просветни работници Анастасија Стојановска, е заменет со Валериј Стефановски.  Беше донесена уште една промена на Деловникот. Бидејќи нема  потпретседател, во отсуство на претседателот  на Собранието негова замена ќе биде најстариот член на Собранието.<br>\r\n    Претседателот на ЗП Центар Злате Стефановски на седницата потсети дека на  20-ти јануари ќе биде на свечен начин одбележан Денот на здружението 22 јануари,  односно големиот јубилеј,  70 години од  формирањето на Здружението. Притоа нагласи дека ова е првото пензионерско  здружение и едно од најголемите во Републикава со најзабележливи резултати низ  овие 70 години.<br>\r\n    На седницата беше информирано дека пензионерите спореде Меморандумот  склучен меѓу Сојузот на здруженија на пензионери на Македонија и приватната  болница Промедика – офталмологија добија можност за офталмолошки прегледи по  намалени цени. Пензионерите од ЗП Центар добиваат попуст и за посета на  театарски претстави во Македонскиот народен театар.</p>', '', 0, 0, 1, 0, '2023-12-23 11:12:12', '2023-12-23 11:12:12'),
(1517, 1, 1, 8, 57, 'Мислење од СЗПМ до Централен регистар на РСМ', 'mislenje-od-szpm-do-centralen-registar-na-rsm', 'ДО ЦЕНТРАЛЕН РЕГИСТЕР', 'Ве известуваме дека Правно -Економскиот форум при Сојузот на здруженија на пензиoнерите на Македонија (во натомошниот текст: СЗПМ) на седницата одржана на 21 11. 2023 година го разгледа прашањето за постапката на упис на Здруженија на граѓаните во Централниот регистер со тежиште на овластувањата и п', '<p>Ве известуваме дека Правно -Економскиот форум при Сојузот  на здруженија на пензиoнерите на Македонија (во натомошниот текст: СЗПМ) на  седницата одржана на 21 11. 2023 година го разгледа прашањето за постапката на  упис на Здруженија на граѓаните во Централниот регистер со тежиште на  овластувањата и постапувањето на Централниот регистер во постапката за упис во  соодветниот регистер.<br>\r\n    Во пристапот кон разгледување на ова тема Форумот тргна од уставно правната  рамка за основање и дејствување на здруженијата на граѓани уредена во Уставот  на РСМ -член 20, Законот за здруженија и фондации и Законот за едношалтарски  систем, како и  Законот за пензиско и  инвалидско осигурување. <br>\r\n    Врз основа на анализата на член 39 од Законот за едношалтерски систем во  кој е уредено постапувањето и овластувањето на Централниот регистер при  разгледување на пријавите за упис во соодветниот регистер, а во корелација со  уставно правната рамка за остварување на правото за слободно здружување на  граѓаните заедно со правилата за нивното основање и дејствување Форумот  го утврди   следното, и тоa: <br>\r\n    <strong>Прво. </strong>Според мислењето на Форумот може да се констатира дека одредбите во членот  39 од Законот за едношалтерски систем, сами за себе и во врска  со споменатите   одредби од Уставот како и одредбитеод ЗЗФ, се нејасни, противречни и  нецелосни на уставно праната рамка за основањето и дејствувањето на здруженија  на граѓани.Имено, според ставот 1 точка 2   од член 39 по приемот на пријавата за упис, регистраторот проверува дали  се исполнети условите за упис определени со закон, потоа точка 4 според која се  проверува дали пријавата ги содржи сите податоци во согласност со закон, како и  точка 5 според која се проверува дали се приложени сите прилози утврдени со  закон и други податоци.<br>\r\n    <strong>Второ</strong>. Меѓутоа, составот 2 на  членот 39  со точките 1,2 и 3 , изрично се наведува дека Централниот регистер не ја  испитува законитоста и вистинитоста на содржината на прилозите (исправите и  доказите), не ја испитува законитоста на постапката во која тие се донесени  ниту ги испитува дали податоците што се запишуваат во регистрите се вистинити и  во согласност со закон.<br>\r\n    Со ставот 3 на овој член за вистиноста и законитоста на прилозите и  податоцитеод ставот 2 на овој член одговара лицето односно лицата определени со  закон.<br>\r\n    Од изложеното произлегува дека одредбите наметнуваат формалистички пристап  при разгледувањето на пријавата и донесување решение за упис без претходно да  се проверуваат и испитуваат дали актите и податоците се во согласност со  законот и се поставува начелно прашање што е смислата на доставување на актот  за основање Статутот и Програмата за работа на здружението, ако претходно  суштински не се проверува нивната содржина и вистинитост, согласно одредбите од  законот за ЗЗФ(член 41-47)<br>\r\n    Тоа значи дека  изрично во законот се предвидени две фази на  остварување на правото на здружување:фаза  на основање и фаза на дејствување, при што и двете фази се под надзор од аспект  на Уставот и ЗЗФ.Значајна е и околноста дека Централниот регистер издава  решенние без да даде образложение.<br>\r\n    Меѓутоа, не е обезбедена проверка на уставноста и законитоста пред уписот  во регистерот, соодветна управно-судска заштита на споменатите уставни  вредности, односно пред да стекнат правен субјективитет за дејствување како  здруженија на граѓани.<br>\r\n    <strong>Трето</strong>. Посебно е прашањето што Законот за едношалтерски систем не дава основа на  Централниот регистер да ги има предвид и одредбите од Законот за ПИО кои се  специфини во однос на одредбите и правилата во ЗЗФ во врска со здружувањето во  Здруженија на пензионери, поврзано со актуелните појави да се регистрираат голем број на  нови здруженија на пензионери со што се  обезвреднува пензионерското организирање.<br>\r\n    Затоа по мислење на Форумот Централниот регистар  би требало да има предвид дека со регистрирање на голем број на здруженија се  раситнува пензионерското организирање како  бидејќи  малите здруженија немаат доволно срества за да  обезбедат активен пензионерски живот,да има инклузивни дејности во практикување  на социјално-хуманитарната дејност и помош како на пензионерот и општеството во  целина.<br>\r\n    Врз основа на погоре утврдени констатации Форумот ги утврди следните:</p>\r\n<p>ЗАКЛУЧОЦИ<br>\r\n    <strong>1</strong>.Наведените констатации упатуват на потребата од доследно  остварување на уставноправната рамка односно концепт за основање и дејствување  на граѓанските организации(во случајот  здруженија на пензионери и сојузи)  за  остварување на нивните цели интереси и дејности но истовремено да се заштитуват  и споменатите уставни и општествени вредности.<br>\r\n    <strong>2</strong>.Правно-Економскиот форум e  на мислење дека при регистирање на здуженија  на пензионери и сојузи на пензионеритe  би требало  да се има внимателен приод бидејќи се работи  за граѓански субјекти кои треба на пензионерите да овозможат активен  пензионерски живот.Ова од причини што на повидок е регистрирање на мали  здруженија на пензионери и сојузи што оправдано загрижува затоа што  малите ЗП   имаат минимален приход кој пред се служи само за покривање на трошоците  за надоместоци на избраните лица,бидејќи немаат доволно средства за  пензионерскиот активизам,во третото доба.<br>\r\n    <strong>3</strong>.Од тие причини се предлага Централниот регистар да  пристапи кон бришење на здруженијата на пензионери кои се пасивни правни  субјекти.<strong></strong><br>\r\n    4. Форумот предлага овие констатации да се имат предвид при  претстојното донесување на новиот Закон за здруженија и фондации и новиот Закон  за едношалтерски систем, а во функција на остварување на уставно начело на  владеење на правото како едно од основните темелни вредности на уставниот  поредок од кое може да се каже  дека во  основа зависи како и дали се остваруваат   другите темелни вредности на поредокот.</p>\r\n<p> Претседател на СЗПМ<br>\r\nСтанка Трајкова</p>', '', 0, 0, 1, 0, '2023-12-23 11:12:12', '2023-12-23 11:12:12'),
(1518, 1, 1, 8, 57, 'Пензионери од две здруженија ја посетија Народна Банка', 'penzioneri-od-dve-zdruzhenija-ja-posetija-narodna-banka', '', 'Претставници на Здружението на пензионери Ѓорче Петров – Скопје и Здружението на пензионери Солидарност-Аеродром на 15 декември 2023 година ја посетија Народната банка на Република Северна Македонија. Во рамки на посетата пензионерите имаа можност да се запознаат со основните функции и задачи на Нар', '<p>Претставници на  Здружението на пензионери Ѓорче Петров – Скопје и Здружението на  пензионери Солидарност-Аеродром на 15 декември  2023 година ја посетија Народната банка на Република Северна Македонија.  Во рамки на посетата пензионерите имаа можност да се запознаат со основните  функции и задачи на Народната банка, современите начини на плаќање електронско  и мобилно банкарство, како и за безбедност при користењето дигитални финансиски  производи и услуги. Посетителите се запознаа со новините од новиот Закон за платежни  услуги и платни системи кој стапи на сила од 1 јануари 2023 година, како и со  бројните едукативни материјали на Народната банка наменети за возрасното  население. <br>\r\n    Исто така, посетителите имаа можност да го  посетат Музејот на Народната банка и да ја видат богатата нумизматичка збирка  на монети коишто се користеле на територијата на нашата земја низ вековите па  се до денес. <br>\r\n    &nbsp;На средбата имаше неколку прашања од  пензионерите. Со цел да им се доставaт одговори прашањата треба да се достават на  е-адреса edukacija@nbrm.mk.  Прашања можат да поставуваат и пензионери кои не беа меѓу посетителите или кои  во иднина ќе ја посетат организирано Народна банка. </p>\r\nСоработката ќе продолжи на  задоволство на пензионерите и на Народна Банка на Република Северна Македонија,  бидејќи посетата од пензионерите е оценета како многу корисна.', 'Narodna Banka.jpg', 0, 0, 1, 0, '2023-12-23 11:12:12', '2023-12-23 11:12:12'),
(1519, 1, 1, 8, 57, 'Го претставуваме спелеолошкиот локалитет „Слатински Извор“ - Споменик на природата', 'go-pretstavuvame-speleoloshkiot-lokalitet-slatinski-izvor---spomenik-na-prirodata', 'ЗП Македонски Брод', 'Убавините во Поречието сè уште доволно не се познати за нашите и странските туристи. Потребата за претставување е многу важна за туристичка промоција за општината Македонски Брод и пошироко за регионот Порече. Покрај пештерата Пешна која е најмногу посетена од пензионерите од Македонија, нудиме уште', '<p>Убавините во Поречието сè уште  доволно не се познати за нашите и странските туристи. Потребата за  претставување е многу важна за туристичка промоција за општината Македонски  Брод и пошироко за регионот Порече. <br>\r\n    Покрај пештерата Пешна која е најмногу  посетена од пензионерите од Македонија, нудиме уште еден локалитет, а тоа е  Слатински Извор кој е  прогласен за  Споменик на природата (Сл. весник бр.23/11), а со право на управување општината  го отстапи на Истражувачкото друштво &bdquo;Урсус спелеос&ldquo; од Скопје. Тој се наоѓа во  синорот на село Слатина оддалечено околу 15 километри од Македонски Брод. Се  патува по течението на реката Треска во еден прекрасен природен амбиент. <br>\r\n    Во рамките на Споменикот на  природата се вбројуваат 5 пештери, едни од најубавите во Македонија па дури на  Балканот. <br>\r\n    Пештерата Слатински Извор е една од  најдолгите пештери со должина од 4 000 метри со река понорница и со богати  подземни убавини:  сталагмити, сталактити и разновидни украси и бои.<br>\r\n    Пештерата &bdquo;Невестинска&ldquo;, а во  народот позната како &bdquo;Пурало&ldquo; има своја легенда, но и е  една од најбогатите подземни уметнички фигури  што ги создала природата. <br>\r\n    Пештерата &bdquo;Горна Слатинска&ldquo; со  должина од 400 метри исто така е  богата  со подземни убавини, а која посетителите можат да ја видат со водичи од СД  &bdquo;Урсус спелеос&ldquo; и ЕД &bdquo;Треска&ldquo; од Македонски Брод. <br>\r\n    Пештерата &bdquo;Овчарска&ldquo; е интересна и  со своја приказна, бидејќи археолошката екипа НУ Археолошки музеј од Скопје  вршеше ископување и утврдила дека се употребувала долг временски период од  праисторијата па се до 20 век како засолниште на овци, по што е добиено и името  на пештерата. Истражувањата се спроведени до 2,5 метри, поточно до пештерското  дно. При сондирањето откриени се многуброен  фрагментиран керамички материјал, згура, јаглен и примероци од типот Пајонски  бронзи. Според стратиграфијата и археолошкиот материјал, слој бр. 1 припаѓал на  средниот век, слоевите 2 до 4 на доцен антички период и во слоевите од 5 до 8  се открива железно допски период во време од 10 до 8/7 век пр. н.е.<br>\r\n    Тука е и петтата пештера наречена  &bdquo;Долна Слатинска&ldquo; која е во тек на истражување. <br>\r\n    Покрај пештерите на овој локалитет  се наоѓаат и познатиот Слатински слап (водопад) на Маркова Река, а во близина и  двата извора на солена вода. Соленицата на двата извора е со своја историја. За  време на Балканските војни и Првата светска војна, Поречаните  од сите села се служеле со солената вода за готвење и месење леб. Имало  воспоставено ред со одговорен стражар кој распоредувал  кое село кога треба да го користи. Покрај водата што се користела во  домаќинството, се користела и како лековита, посебно за фрактури на глава,  раце, нозе која и денес безброј посетители од разни краеви на Македонија  доаѓаат да ја видат и користат. До водопадот и солените извори изградена е  уредна и пристапна патека со патокази и поставени се клупи и маси за одмор на  посетителите. Затоа до сите пензионери ширум Македонија упатуваме покана да го  посетат Споменикот на природата &bdquo;Слатински Извор&ldquo; за да се уверат во убавините  што ги нуди овој локалитет.</p>', 'M.Brod slatinski izvor2.jpg', 0, 0, 1, 0, '2023-12-20 11:12:12', '2023-12-20 11:12:12'),
(1520, 1, 1, 8, 57, 'Мала помош која многу значи', 'mala-pomosh-koja-mnogu-znachi', 'НОВ ПРОЕКТ НА СЗПМ', 'Потребата да се предложи овој проект насловен како “Мала помош која многу значи“ e маката на една пензионерка која две седмици немаше кој да и ја смени сијалицата во ходникот бидејќи е високо поставена, а таа не смее и не може да користи скала. Такви примери има многу. Истото се случува и со други м', '<p>Потребата  да се предложи  овој проект  насловен како <strong>&ldquo;Мала помош која многу значи&ldquo; </strong>e  маката на една пензионерка која две  седмици  немаше кој да и ја смени  сијалицата во ходникот бидејќи е високо поставена, а  таа не смее и не може да користи скала. Такви примери има многу. Истото   се случува и со други  мали поправки во домот на пример: да се смени  гумичка  на чешмта или да се помогне да  се закачи на ѕид слика која е значаен спомен од младоста, да се изврши мала  поправка на правосмукалката, да се поправи ролетната која се откачила и уште  многу други мали нешта кои им го загорчуваат животот особено на највозрасните и  немоќни осамени наши колеги членови на нашите здруженија. <br>\r\n    Посетата на осамен  пензионер ќе биде добредојдена  и  пријатна и за тој што помага и за тој на кој му се помага. При тоа бонус е  дружењето! При тоа посетителот ќе биде пензинер кој ќе го знае здружението, предложен  од соодветна комисија во здружението по претходно утврдени критериуми.  <br>\r\n    Значи, овој Проект   ќе биде можност  да се искористи занаетчиското и друго знаењето  на пензионери кои ќе сакаат да ги направат овие ситни услуги, многу хумани и  важни за пензионерот примател на услугата. <br>\r\n    Се предлага  најпрво овој проект да се реализира во еден огранокот бидејќи  пензионерите  таму се познаваат и полесно е да се организраат   како ПИЛОТ ПРОЕКТ. Ако се покаже корисен  односно пензионерите бидат задоволни, може да се прошири и во другите ограноци  на одредено здружение.<br>\r\n    <strong>За овој  проект Здружението на пензионери ќе донесе соодветна правна рамка која ќе  содржи одредени потребни елементи: каде ќе започне да функционира проектот, евиденција  на ангажираниот пензинер, легитимација која ќе ја поседува ангажираниот  пензионер во која ќе пишува од кое Здружението на пензионери е ангажиран и  друго.  Потоа ќе се направи анализа со согледување  на извршените волонтерски активности. При тоа во овој проект многу значајно ќе  биде  и одредена СИМБОЛИЧНА  СТИМУЛАТИВНА  ПОДРШКА  од страна на здружението во вид на директни  трошоци на ангажираниот пензионер-помагач односно извршител на одредена  активност. Учесници на проектот може да биде и  група на &bdquo;помагатели</strong><strong>&rdquo;</strong><strong> со различни  занаетчиски знаења.</strong><br>\r\n    <strong>Со овој  хуман Проект уште еднаш ќе се  потврди  солидарноста на возрасните во секое здружение бидејќи факт е дека секој има  потреба од некоја мала помош за која тој што ќе ја добие значи многу! </strong><br>\r\n    Се смета дека овој  проект ќе заживее особено во оваа 2024 ГОДИНА КОЈА E ПРОГЛАСЕНА ЗА ГОДИНА НА  ОГРАНОКОТ. <br>\r\n    Пензионерите и  воопшто возрасните животот ги научил дека луѓето треба да си помагаат секогаш  во секоја прилика бидејќи само така може да се опстои, така впрочем опстоило и  човештвото.  Интересно е дека дури и  растенијата и животните си помагаат. За тоа има многу интересни приказни. <strong></strong><br>\r\n    Сега многумина се  упатени на комшии, пријатели или познати, но секој не се пушта во својот дом.  Ако &bdquo;поправачот&ldquo; е од вашето здружение се&rsquo; е поинаку и попријатно. Затоа да се  обидеме и уште еднаш преку овој Проект да ја покажеме нашата солидарност на  дело. </p>', '', 0, 0, 1, 0, '2023-12-23 11:12:12', '2023-12-23 11:12:12'),
(1521, 1, 1, 8, 57, 'Месеци исполнети со активности во ЗП Валандово', 'meseci-ispolneti-so-aktivnosti-vo-zp-valandovo', '', 'По завршувањето на Републичките спортски пензионерски игри на кои ЗП Валандово настапи со претставници во три диспиплини, освои едно второ место и тоа во дисциплината шах-жени. Ова е сепак успех за едно мало здружение како што е Валандово и предизвик да се работи на постигнување подобри резултати, З', '<p>По&nbsp; завршувањето на&nbsp;  Републичките спортски пензионерски игри на кои ЗП Валандово&nbsp; настапи со  претставници во три диспиплини, освои едно второ место и тоа во дисциплината  шах-жени. Ова е сепак успех за едно мало здружение како што е Валандово и  предизвик да се работи на постигнување подобри резултати, Здружението продолжи  со активностите во своето работење. Одржани се две седници на Извршниот одбор  на ЗП, на кои се донесоа повеќе одлуки, меѓу кои доделување солидарни пакети за  Денот на старте лица и&nbsp; за Новогодишните и Божиќни празници,&nbsp;  еднократни парични помошти, отстапување на мебел-маси и столови за отворениот  клуб во огранокот &bdquo;Јосифово&ldquo;,&nbsp; учество на музичката ревија &bdquo;Пензионерите  пеат&ldquo;&nbsp; во Росоман,&nbsp; организирање екскурзија до Крушево, и слично. <br>\r\n    На седниците исто така, беа  разгледани направените финансово-материјални трошоци за периодот&nbsp; јануари  – јуни и јануари –октомври,&nbsp; Програмата за работа на Здружението и  финансовиот план за 2024 година, како и други тековни прашања. <br>\r\n    За Денот на&nbsp; старите лица, беа  поделени 50 пакети со прехрамбени производи на најстарите членови на  Здружението и тоа на лицата родени до 1932-1933 година.&nbsp; За истакнување е  дека во општината имаме едно лице родено 1926 година, која исто така беше  посетена од активистките на Здружението. <br>\r\n    На 28. октомври 2023 во оргнизација  на Локалната самоуправа и Градоначалникот на Општината, се организира  традиционалната манифестација &bdquo;Ракијада2023&ldquo;&nbsp; на која активистки од  Здружението беа ангажирани за организација на пречек на гостите, меѓу кои и беше  Премиерот на РСМ, Димитар Ковачевски. <br>\r\n    На 6–ти ноември Денот на  ослободувањето на Валандово,&nbsp; делегација на ЗП&nbsp; положи цвеќе на  споменикот на паднатите борци од НОБ, во градскиот парк &bdquo;Пеонија&ldquo; во  Валандово.&nbsp; Во текот на месецот&nbsp; повторно имавме акција за доделување  на пакети во огранокот &bdquo;Пирава&ldquo; на лица на кои им е неопходна помош, болни,  осамени лица со најниски пензии. На покана од ЗП Штип, со кого имаме  воспоставено&nbsp; добра соработка, музичката група &bdquo;Распеани&nbsp; Пензионери&ldquo;  настапија на концертот на &bdquo;Пензионери-ветерани&ldquo; организиран од ЗП Штип. Во организација  на Активистки од Дојран, беше организирана манифестацијата &bdquo;Дојранска Софра&ldquo; на  која&nbsp; присуствуваа околу 30 пензионерки од&nbsp; ЗП Валандово. Активностите&nbsp;  ги заокруживме со&nbsp; еднодневната екскурзија до Крушево, каде&nbsp; група од  50 пензионери поминаа еден прекрасен ден, во убавото Крушево и на кратка  прошетка низ Прилеп. <br>\r\n    Активностите ги продолживме и во  текот на овој месец, со тоа што ќе се одржат седници на Извршниот одбор и  Собранието, ќе се организира предновогодишна прослава за пензионерите, а нема  да ги заборавиме ни пензионерите кои се сами, болни и оставени сами на себе. Активностите  продолжуваат и понатаму. </p>', 'Valandovo aktivnosti.jpg', 0, 0, 1, 0, '2023-12-19 11:12:12', '2023-12-19 11:12:12'),
(1522, 1, 1, 21, 57, 'XXVII пензионерски спортски игри 2023', 'xxvii-penzionerski-sportski-igri-2023', '', '', '', 'BILTEN-SPORT-2023-NASLOVNA.jpg', 0, 0, 1, 0, '2022-12-31 11:12:12', '2022-12-31 11:12:12'),
(1523, 1, 1, 8, 57, 'Известување за посмртна помош', 'izvestuvanje-za-posmrtna-pomosh', '', 'Висината на исплатата на посмртната помош што се исплатува на семејството на починатиот пензионер изнесува 35.000 денари за починатите пензионери од 1 јануари 2024 година', '<p>Висината на задршката за солидарен фонд - посмртна помош изнесува 140 денари за периодот од 1 јануари до 30 јуни 2024 година.<br>\r\n    Висината на исплатата на посмртната помош што се исплатува на семејството на починатиот пензионер изнесува 35.000 денари за починатите пензионери од 1 јануари 2024 година.</p>\r\n<p><a href=\"/vesnik/dokumenti/sluzben vesnik 12.2023.pdf\" target=\"_blank\">(објавено во сл. весник на 27 декември 2023 год)</a></p>', '', 0, 0, 1, 0, '2024-01-02 11:12:12', '2024-01-02 11:12:12'),
(1524, 1, 1, 22, 57, 'KOХA јануари 2024', 'koha-januari-2024', '', '', '', 'koha2024.01.jpg', 0, 0, 1, 0, '2024-01-23 11:12:12', '2024-01-23 11:12:12'),
(1525, 1, 1, 24, 57, '186', '186', '', '', '', '186.jpg', 0, 0, 1, 0, '2024-02-18 11:12:12', '2024-02-18 11:12:12'),
(1526, 1, 1, 8, 57, 'Данче Даскаловска, претседателка за пример', 'danche-daskalovska-pretsedatelka-za-primer', 'ЗП Струмица', 'Колку години некој ќе е на чело на здружението не е битно ако здружението реди резултати и успеси. Од друга страна според мене малку е еден мандат за некои поголеми и посериозни потфати како што е изградба на дом.', '<ul type=\"disc\">\r\n    <li><strong>Почитувана Данче вие сте претседателка на Здружението       на пензионери Струмица кое е формирано во далечната 1953 година, од група  ентузијасти пензионери од Струмица, со       име Општинско здружение на пензионери Струмица. Кажете ни кои се глави       карактеристики на оваа здружение?   </strong></li>\r\n</ul>\r\n<p>-Здружението на пензионери Струмица има  околу11 985 членови, организирани во 27 ограноци, во нив членуваат пензионери  од Струмица, Василево и Босилово.</p>\r\n<p>На територијата на ЗП Струмица  функционираат 17 клубови од кои по 3 во Општините Василево и Босилово и 11 во  Струмица. Акти по кои работи Здружението на пензионерите Струмица: Статут, Деловник  за работа на Собранието, Деловник за работа на Извршниот одбор, Правилник за  еднократна парична помош и Годишна Програма за работа. Органи на ЗП Струмица:  Собрание кое брои 61 член, Извршен одбор од 15 членови.  Постојат следните комисии: Комисија за  статутарни и правни работи, за материјална помош, за културно-забавен живот, за  спорт и рекреација, во кои членуваат по 5 членови. Во здружението се формираат  и работат и други повремени комисии. Здружението секоја година носи Финансиски  план според Програмата за работа, Извештај и Завршна сметка за годината која  изминува..</p>\r\n<ul type=\"disc\">\r\n    <li><strong>Вашето здружение е особено познато по       Пензионерскиот дом кој го поседува вашето здружение имот кој вие како       претседател го надградивте и проширивте кажете ни нешто за ова со кое со       право се гордеете.</strong></li>\r\n</ul>\r\n<p>- Во 1969 година тогашниот управен  одбор на здружението на иницијатива на Марика Ѓорѓиева донесува одлука за  изградба на Дом на пензионери во Струмица и  истата година е ставен камен-темелникот на  домот. Во октомври 1971 година Општинското здружение на пензионери Струмица  добива новоизграден дом на улица Ленинова бр. 97 во Струмица. Во декември 1998  година со одлука на Собранието Општинското здружение на пензионери Струмица се  преименува во Здружение на пензионери Струмица. Денес ЗП Струмица е сопственик  на пензионерски дом кој содржи: работни простории, деловен простор од 385 метри  квадрати и 10 гарсоњери во кои се сместени пензионери членови на здружението. Во  2021 година зградата за домување е целосно реновирана.  При реновирањето изменета е водоводна,  канализациска и електрична инсталација, комплетно внатрешно уредување на  становите, заменет е покривот, изработена е нова фасада и уредено е дворното  место. За оваа намена потрошени се 7.373.484 денари средства на здружението. Во  првото тромесечие од 2022 година реновиран е клубот за дневен престој на  пензионерите во село Дабиља. За оваа активност здружението обезбедило 1.012.100  сопствени средства.</p>\r\n<ul type=\"disc\">\r\n    <li><strong>Со кои други активности ќе се пофали ЗП Струмица       со кое вие раководите повеќегодишни?</strong></li>\r\n</ul>\r\n<p>- Колку години некој ќе е на чело на  здружението не е битно ако здружението реди резултати и успеси. Од друга страна  според мене малку е еден мандат за некои поголеми и посериозни потфати како што  е изградба на дом. Што се однесувало други активност ЗП Струмица секоја година  доделува парична помош во износ од 3.000 до 10.000 денари на болни пензионери и  пензионери со ниски пензии.  Здружението  континуирано дава сугестии и предлози за актуелни прашања со невладини  организации и други институции. Исто така настојува лекари специјалисти да  држат предавања пред пензионерите. Предавањата се одржуваат во клубовите на  пензионерите, по предлог на раководствата на ограноците. Во таа смисла постои  координација на ограноците.  Врз основа  на потпишани спогодби за меѓусебна соработка со општините Струмица, Босилово и  Василево и ЗП Струмица Здружението предлага и склучува спогодби за соработка од  заеднички интерес. Учествуваме во активности организирани од СЗПМ како што се  регионалните и републичките ревии на песни, музика и игри, на некои од нив било  и успешен домаќин, како и на спортските натпревари и дава свој придонес во  органите и телата на Сојузот.</p>\r\n<p>ЗП Струмица соработува и со други здруженија, членки на  СЗПМ, како и со институции поврзани со пензионерската популација како што се ОО  на Црвениот крст и Центарот за социјални грижи. Здружението во соработка со Црвениот  крст и други невладини асоцијации на локално ниво развива активности во повеќе  сегменти, и тоа:  вклучување на  пензионери во разни проекти за помош на стари лица, посета на истите и друго. По повод верскиот празник &bdquo;Тримери&ldquo; Здружението организира  собир на кој учествуваат голем број пензионери од повеќе здруженија, членки на  СЗПМ.    </p>\r\n<p>Комисијата за културно-забавен живот  организира посети на манастири и други екскурзии по избор на пензионерите низ  Македонија, како и надвор од државата.  .</p>\r\n<p>ЗП Струмица го информира своето  членство преку печатот и регионалните телевизии  и весникот &bdquo;Пензионер плус&ldquo;.</p>\r\n<ul type=\"disc\">\r\n    <li><strong>Препознаена ли е вашата работа од       пензионерите членови на здружението, Сојузот и општината Струмица?</strong></li>\r\n</ul>\r\n<p>- Да, и на тоа сум  особено горда. Имам добиено многу признанија, дипломи плакети, а посебно ми е  драга наградата што ја добив за животно дело од Општината Струмица  минатата  2023 година. Се на се моите  резултати докажуваат што се може да постигне една жена претседател на  здружение, но и пошироко.</p>\r\n<p>Претседател на  здружението и на ИО е Данче Даскаловска,  претседател на Собранието е Атанас Крстев, а  администратор е Моме Лазаревски. </p>', 'dance daskalovska.jpg', 0, 0, 1, 0, '2024-03-22 11:12:12', '2024-03-22 11:12:12'),
(1527, 1, 1, 8, 57, '„Работно време“  во здруженијата на пензионери и  во сојузот', 'rabotno-vreme-vo-zdruzhenijata-na-penzioneri-i-vo-sojuzot', 'НАЈАВА', 'Прашањето на  “работното време”  во здруженијата и во Сојузот повторно  се наметнува со својата актуелност и значење.', '<p>Прашањето на  &ldquo;работното време&rdquo;  во здруженијата и во Сојузот повторно  се наметнува со својата актуелност и значење.  Се поставува прашањето колку треба да трае работното време во здруженијата и во  Сојузот. Дали ова прашање треба да биде уредено со општа правна рамка на  Сојузот со утврдени мерила и критериуми.</p>\r\n<p>Ако се направи  извесна дигресија пензионерите  своите активности  ги остварувале  во рамките   на други здруженија,  за по  одреден временски период да формираат и свое здружение, а потоа и сојуз како  повисок облик на организирање.</p>\r\n<p>Гледано наназад   низ  годините  сите членови во здружението  имале еднакви права и должности. Секој член  имал активно и пасивно избирачко право, како и да користи помош врз основа на  актите на здружението, да ги користи читалните и библиотеките  и да учествува во првите традиционални  културно-забавни и спортски активности, да добива материјална поддршка и друго.</p>\r\n<p>При тоа, многу значајна  била околноста  што во статутите била  утврдена обврската членовите да го чуваат угледот на здружението. Постоела  заложба да се  спречува секоја негативна  појава која би го нарушила угледот на здружението и пошироко на општеството.</p>\r\n<p>Во прво време  здруженијата имале помал број членови и работеле 2-3 дена во неделата,  активностите пред сè ги извршувале волонтерски, а како што се зголемувал бројот  на членовите, се зголемувале и обврските  на избраните лица. Како резултат на тоа покрај  волонтерската работа се исплатувале и симболични надоместоци.</p>\r\n<p>Сега, во 2024  година, кога имаме околу 290 000 пензионери кои членуваат во 53 здруженија,  членки на Сојузот, со голем број активности, пензионерски клубови и ограноци,  со значење се наметнува траењето на РАБОТНОТО ВРЕМЕ  како во здруженијата, така и во Сојузот.  Ако  постојано   сме во потрага по нови активности и да покажеме дека е оправдано нашето  постоење, тогаш значајно е колку трае нашето работно време. Дали треба да  дозволиме  пензионерот-член да дојде во ЗП,  а вратите да бидат затворени? Дали треба да се толерира летно време  претседателот да &ldquo;одмора&rdquo; три месеци или да се штеди на електрична енергија, и  тоа од поголеми здруженија на пензионери, и клубовите да бидат затворени!?</p>\r\n<p>Иако во случајот  не станува збор за  класично   работно  време, тоа не значи дека треба цел летен период да се затворени вратите или да  се работи само 2-3 часови, а да се исплатува цел месечен надоместок. Приодот  кон ова прашање треба да е посериозен и поодговорен. Верувам дека ова прашање  ќе се разгледа  и во здруженијата, и на  истото ќе му се посвети сериозно внимание, заради  оправдување на дадената доверба од членството.\r\n<strong>Станка Трајкова, претседателка на СЗПМ  </strong></p>', '', 0, 0, 1, 0, '2024-02-12 11:12:12', '2024-02-12 11:12:12'),
(1528, 1, 1, 8, 57, 'Интервју со Менка Темелковска, секретар на ИО на СЗПМ, повод 8 март, Меѓународниот ден на жените', 'intervju-so-menka-temelkovska-sekretar-na-io-na-szpm-povod-8-mart-megjunarodniot-den-na-zhenite', '', 'Жените се застапени се’ повеќе на сите нивоа во здруженијата на пензионери и во Сојузот', '<ul>\r\n    <li><strong>Почитувана  Темелковска, најнапред Ве молам, кажете им на нашите читатели што се Вашите ингеренции  и задачи како секретар на Извршниот Одбор, една од четирите раководни функции  во Сојузот на здруженија на пензионери на Македонија. Која беше Вашата  мотивација да ја прифатите оваа важна функција?</strong></li>\r\n</ul>\r\n<p>- Функцијата  секретар во Сојузот на здруженија на пензионерите на Македонија е изборна  функција која се бира на четири години. На оваа функција сум избрана по вторпат  бидејќи беше препознаена од членството мојата стручност и трудољубивост.  Изборот ми претставува чест, но довербата треба да се оправда.</p>\r\n<p>За прифаќање на  функцијата секретар на Сојузот, мотивација ми беше мојата претходна соработка  со избраните лица во Сојузот. Имено, додека бев во работен однос, редовно  соработував со лицата во Сојузот, со доставување на податоци за пензионерите,  усогласување на пензиите, доставување на средствата за посмртна помош и  членарина и други податоци.</p>\r\n<p>Како секретар во  Сојузот на здруженија на пензионерите на Македонија ги следам прописите од  пензиското и инвалидското осигурување, економските движења кои влијаат на  висината на пензиите, подготвувам информации за висината на пензиите споредена  со висината на платите на сите вработени, како и движењето на трошоците на  животот со споредбени податоци од бившите југословенски републики. Го следам  финансиското работење на Сојузот и подготвувам периодични  финансиски извештаи и годишен деловен  извештај за целокупното работење на Сојузот. кои се разгледуваат на седниците  на органите и телата, Учествувам во подготвување на материјали за социјално  домување на пензионерите, исплатата на солидарниот фонд, хуманитарните  активности и други задачи.   Раководам со  Стручната служба на Сојузот, се грижам за навремено подготвување и доставување  на сите материјали на седници на органите и телата како и на избраните  делегати.</p>\r\n<ul>\r\n    <li><strong>Вие на  оваа функција доаѓате од Фондот за ПИОМ со добро познавање на работите од овој  домен. Што мислите треба ли нешто да се менува во системот и што? </strong></li>\r\n</ul>\r\n<p>- Во системот на  пензиското и инвалидското осигурување сметам дека треба да се изврши натамошно  подобрување на формулата за усогласување на пензиите. Во моментот усогласување  се врши од 1 март и 1 септември тековната година, наместо тоа да се врши од 1  јануари и 1 јули. Со сегашната формула се закинува усогласувањето на пензиите  за 4 месеци (јануари и февруари и јули и август). Исто така во услови на  значителен пораст на платите, сметам дека треба да се примени формула на  поголемо зголемување на пензиите на ниските пензиски примања за што треба да се  создаде законски основ.</p>\r\n<ul type=\"disc\">\r\n    <li><strong>Покрај што ја објавувате оваа       функција имате ли и други задолженија во Сојузот и надвор од него? </strong></li>\r\n</ul>\r\n<p>- Во Сојузот  учествувам во работата на Извршниот одбор, Собранието на Сојузот и во  работата  Статутарната комисија,  Правно-Економскиот форум, Комисијата за старски и пензионерски домови и  стандард. Надвор од Сојузот оваа година бев член на Работната група за  разгледување на предлози за прашањето за најниската пензија во РСМ и пакет  мерки за помош и поддршка на пензионерите. Исто така работам и на аплицирање на  проекти на кои аплицира Сојузот. Последен проект на кој работев и го добивме од  Европската Унија заедно со Црвениот крст беше за животот на руралните  пензионери. </p>\r\n<ul type=\"disc\">\r\n    <li><strong>Сојузот на здруженија на пензионери       на Македонија е најголемата невладина мултиетничка асоцијација  во која членуваат околу 330 000       пензионери. Колкав е бројот на жени и во кои активности тие го држат       приматот? Една од главните карактеристики на Сојузот е волонтерството.       Кажете ни како функционира тоа во Сојузот и неговите 53 здруженија?</strong></li>\r\n</ul>\r\n<p>- Бројот на жените  како претседателки во здруженијата на пензионери и во Сојузот постепено започна  да се менува. Со претходните избори за прв пат претседател на Сојузот беше  избрана жена, додека во здруженијата на пензионери од 53 претседатели на  здруженија само во 6 здруженија претседателки беа жени. Со последните избори  2023-2027 година бројот на жени претседателки на здруженија изнесува 12 што е  двојно зголемен. Жени се застапени се&rsquo; повеќе во сите органи и тела на  здруженијата на пензионери, како и во Сојузот. Се разбира дека нивната  застапеност треба да биде уште поголема. На тој план работиме сите и има  резултати. </p>\r\n<ul type=\"disc\">\r\n    <li><strong>Една од главните карактеристики на       Сојузот е волонтерството. Кажете ни како функционира тоа во Сојузот и       неговите 53 здруженија?</strong></li>\r\n</ul>\r\n<p><strong>- </strong>Главни носители на  волонтерството во Сојузот се жените кои секогаш се подготвени да подадат рака  на оној на кој тоа му е потребно. Посетуваат болни и изнемоштени пензионери,  Домови за стари, делат хуманитарни пакети, соработуваат со здруженија на деца  со посебни потреби, организираат хуманитарни базари на кои продаваат свои  производи и друго. Особено соработуваат со Црвениот крст и со други организации  кои се исто така, формирани на волонтерска база. Жените во Сојузот и  здруженијата се синоним за волонтерство и хуманост.</p>\r\n<ul>\r\n    <li><strong>Вие сте  жена, мајка, баба, сопруга и активна пензионерка. Како го постигнувате сето  тоа? Имате ли време за се&rsquo;? Што правите во слободното време?</strong></li>\r\n</ul>\r\n<p>- Во слободното  време читам книги, правам кратки прошетки, се дружам со моите пријателки и се  грижам за моето домаќинство. Кога нешто се сака, секогаш може да се најде  време. Јас сум по професија економист, рационална личност и со добро планирање и  организација, се трудам да  ги обавувам и  сите овие  &bdquo;функции&ldquo;, се надевам успешно. </p>\r\n<ul>\r\n    <li><strong>Од сето  тоа што го кажувате Вие сте една успешна жена- пензионерка иако сте во третата  доба. Кажете има го Ве молам и на другите жени-пензионерки рецептот како може да  се биде успешен на повеќе полиња во исто време.</strong></li>\r\n</ul>\r\n<p>- За успешна жена  нема посебен рецепт, тоа е индивидуално според можностите и желбите на секоја  жена. Мојот рецепт е се&rsquo; што се работи да се работи со посветеност и љубов.  Успехот неминовно следи. Исто така за успешност потребно е и постојано  надградување на се&rsquo; што се знае и што е потребно за да се добијат подобри  резултати. </p>\r\n<ul>\r\n    <li><strong>Што  мислите како треба да се одбележува 8. Март, Меѓународниот ден на жената? </strong></li>\r\n</ul>\r\n<p>- Осми март,  Меѓународниот ден на жената сметам дека треба да се одбележи работно, но може  да се направи и прослава во зависност од желбите на секоја жена поединечно. </p>\r\n<p>Според мене жената  треба да се почитува за сето она што го прави за семејството и општеството  секој ден, а не тоа да се случува само на овој ден. Таа тоа го заслужува.</p>', 'menka temelkovska.jpg', 0, 0, 1, 0, '2024-03-22 11:12:12', '2024-03-22 11:12:12');
INSERT INTO `records` (`id`, `id_user_logged`, `id_user`, `id_module`, `id_association`, `title`, `slug`, `subtitle`, `intro`, `text`, `picture`, `main`, `cover`, `active`, `deleted`, `created_at`, `updated_at`) VALUES
(1529, 1, 1, 8, 57, '12 претседателки на здруженија на пензионери во СЗПМ', '12-pretsedatelki-na-zdruzhenija-na-penzioneri-vo-szpm', '', 'Во Сојузот на здруженија на пензионери на Македонија по 78 години постоење оваа 2024 година го дочекавме 8. Март со 12 претседателки од 53 здруженија на пензионери или во проценти 22%, за разлика од пред 20 години кога имало само 4 претседателки на здруженија или само 8%. При тоа сите здруженија на кои претседателка е жена нижат успеси и резултати.', '<p>Во Сојузот  на здруженија на пензионери на Македонија по 78 години постоење оваа 2024 година  го дочекавме 8. Март со 12 претседателки од 53 здруженија на пензионери или во проценти  22%, за разлика од пред 20 години кога имало само 4 претседателки на здруженија  или само 8%. При тоа сите здруженија на кои претседателка е жена нижат успеси и  резултати. </p>\r\n<p>Овој подем  и застапеност на жени за претседателки особено е забележителен откако на чело на  Сојузот за првпат после толку години е жена, истакнат и долгогодишен активист и  експерт во доменот на инвалидско-пензионерско осигурување и во пензионерското организирање,  Станка Трајкова.</p>\r\n<p><img src=\"/vesnik/images/info/12-penzionerki.jpg\" alt=\"\"/></p>\r\n<p>    Зошто овие 12 претседателки  се нафатиле да бидат на чело на едно здружение и покрај големата одговорност и потреба  да се справат со повеќе предизвици. Еве ги нивните одговори</p>\r\n<h3>ЗП Битола<br>\r\n    претседател Даница Петличкова </h3>\r\n<p>Главна мотивација да ја прифатам оваа функција ми беше Конвенција  за отстранување на сите облици на дискриминација врз жени на ООН, според која треба  да се реализираат сите права и слободи на жените врз основа на рамноправност со  мажите. Тргнувајќи од оваа Декларација, како претседателка на најбројното здружение  во Сојузот и со најдолга традиција на пензионерско организирање, мојата цел е и  ќе биде вклучување на жените пензионерки во сите сфери на општественото живеење  како и во сите органи и тела на пензионерското организирање. Активности имаме во  сите сфери на пензионерското организирање. Со мое лично залагање и на повеќе членови  од ЗП Битола, даваме придонес и во органите на Сојузот. </p>\r\n<h3>ЗП Валандово <br>\r\n    претседател Горица Михајлова </h3>\r\n<p>Како вработена во ОВР на РМ во пензија отидов релативно рано,  заради бенифициран стаж. Веднаш се вклучив во здружението на пензионери. Се нафатив  да бидам претседателка бидејќи сметав дека имам енергија, знаење и искуство да придонесам  за поактивно стареење на пензионерите од ЗП Валандово. Не бев веднаш претседателка,  но моите заложби беа препознаени и сега со одговорност ја извршувам оваа значајна  функција: претседателка на здружението. Постигнуваме видливи резултати на спортски,  културен и хуманитарен план. Според мене животот на највозрасните стана посодржаен.</p>\r\n<h3>ЗП Виница <br>\r\n    претседател Павлина Ангеловска</h3>\r\n<p>По професија сум економист. Се нафатив да бидам претседател  на Здружението со желба финансиски да го средам. Откога сум претседателка клубот  на пензионери заживеа, а ќе отвориме и нови клубови. Подем има и културно-забавниот  живот оформувавме игроорна и пејачка група со која ќе настапуваме. Ќе настапиме  на регионалните ревии на песни, музика и игри, како и на спортските натпревари.  Ќе ги организираме сите зацртани програмски цели. Резултати има, а мислам дека ќе  ги има уште повеќе во иднина во интерес на луѓето од третата доба. </p>\r\n<h3>ЗП Гази Баба <br>\r\n    претседател Стојна Тодоровска </h3>\r\n<p>Во ЗП Гази Баба долги години сум инволвирана во сите случувања  и активности и ги познавам проблеми. По професија сум економист и ги знам работите  и од тој аспект. Сето ова поткрепено од членството и нивната желба да ја прифатам  оваа важна функција ме мотивираше да прифатам. Работењето ме исполнува и работам  со задоволство. Ме радува кога ќе видам насмевка на нечие лице и го нема чувството  на осаменост. Активни сме на секој план. Соработуваме со СЗПМ, со Црвениот крст,  и со други здруженија со кои сме збратимени. Неодамна го прославивме големиот јубилеј  на здружението: 51 година постоење. </p>\r\n<h3>ЗП Делчево <br>\r\n    претседател д-р Виолета Арсова </h3>\r\n<p>Се нафатив да ја извршувам оваа одговорна и важна функција  со желба луѓето од третата доба да имаат подобар живот, а пред с¢ да бидат поздрави.  Како некогашен лекар по професија, со помош на мојот авторитет пронаоѓам начини  да им создадам на моите возрасни сограѓани пријатно катче каде ќе го поминуваат  своето време, а како лекар се трудам да ја подигнам нивната свест за превенирање  и чување на нивното здравје со совети, организирање предавања и друго. Со други  зборови се трудам да ја оправдам довербата што ми ја дадоа. </p>\r\n<h3>ЗП Кочани претседател <br>\r\n    Јелица Зафирова </h3>\r\n<p>Мојата главна мотивација да бидам претседателка на ЗП Кочани  беше да го заживеам здружението со воведување нови форми и содржини, со нивното  омасовување со релативно помлади членови, особено жени. Иако знаев дека сето тоа  бара многу труд и ангажирање, не се двоумев. Резултатите доаѓаат на задоволство  на сите. За време на моето претседателствување успешно се одржа Републичка ревија  на песни, музика и игри во Кочани, а нашето здружение со успех ја претставуваше  нашата земја, градот и СЗПМ на Меѓународниот фестивал за трето доба во Љубљана,  Р Словенија. </p>\r\n<h3>ЗП Лозово <br>\r\n    претседател Марија Моневска </h3>\r\n<p>Се кандидирав за претседател на здружението на пензионери  затоа што чувствував дека имам капацитет да ја вршам оваа одговорна функција. Исто  така имав голема желба ЗП Лозово да го извлечам од анонимноста и да го ставам на  ниво кое здружението иако е мало, тоа го заслужува. Мислам дека успеав и заради  тоа сум многу горда. Организираме повеќе средби, екскурзии и манифестации, а во  моментов сум и претседателка на Координативното тело на Активи на пензионерки при  СЗПМ. И оваа функција ја извршувам одговорно и сум горда на довербата што ми е дадена. </p>\r\n<h3>ЗП Македонска Каменица <br>\r\n    претседател Јаворка Соколовска </h3>\r\n<p>Кога одлучив да се нафатам на барање на членството да бидам  претседателка на здружението решив да се посветам на збогатување на сите активности,  а особено на хуманитарните активности. Иако сме релативно мало здружение не заостануваме  во ништо и со другите активности. Го подобривме квалитетот на животот на пензионерите,  преку разни манифестации и дружења на кои доаѓаат голем број пензионери од речиси  сите здруженија членки на Сојузот. Имаме добра соработка и со Општината, со градоначалникот  и со други институции во интерес на нашето членство. </p>\r\n<h3>ЗП Пехчево <br>\r\n    ретседател Павлина Цековска </h3>\r\n<p>Пензионерскиот живот без ангажирање на сите може да биде здодевен.  За да има случувања потребно е да се заложат сите. Бидејќи сметав дека можам да  придонесам да се воведат нови активности и животот на луѓето во трето доба да биде  поинтересен, ја прифатив оваа функција и ја извршувам според моите можности. Сметам  дека е мојот труд го препознава членството. При моето доаѓање на оваа функција воведовме  парно греење и реновиравме дел од некои инсталации во здружението.</p>\r\n<h3>ЗП Радовиш и Конче <br>\r\n    претседател Ефтерпи Димитрова </h3>\r\n<p>Ја прифатив оваа функција и со задоволство ја извршувам. И  самото мое име &ldquo;Муза на поезија# упатува кои се главните преокупации во мојата работа  како претседателка на здружението. Успеав да го подигнам квалитетот на културниот  живот на највозрасните. Оформивме клуб на читатели на книгата, склучивме договори  за пониска цена на претстави во театрите во Радовиш и Штип, и националната опера  и балет, а особено сум горда на легализација на имотот на здружението. Чест ми е  што ја добив оваа функција и воопшто не го жалам мојот труд и потрошена енергија.</p>\r\n<h3>ЗП Струмица претседател <br>\r\n    Данче Даскаловска </h3>\r\n<p>Не е лесно да се биде претседател на здружение на пензионери.  Бара многу енергија и заложби и издвојување на време што можеш да го искористиш  за други работи, но кога ќе се видат резултатите и дека си оставил работи кои ќе  се паметат, с¢ се заборава. Да се заокружат одредени сериозни проекти бара да се  биде подолго на чело на здружението. Тоа главно ме мотивираше да бидам претседател  уште еден мандат.</p>\r\n<h3>ЗП Шуто Оризари <br>\r\n    претседател Љиљана Хајдар </h3>\r\n<p>Долго време сум активна на сите полиња во здружението на пензионери.  Ги познавам и случувањата и можностите на нашето здружение. Од таа причина се нафатив  да бидам претседателка на здружението и да помогнам тоа да опстојува и да се развива.  Додатно на тоа, сакав да докажам дека и ромската жена е и способна и има капацитет  да ја врши оваа важна функција која ја извршувам. Во нашето здружение опфатени с¢  повеќе жени особено во играорната група.</p>', '12-penzionerki.jpg', 0, 0, 1, 0, '2024-03-24 11:12:12', '2024-03-24 11:12:12'),
(1530, 1, 1, 24, 57, '187', '187', '', '', '', '187.jpg', 0, 0, 1, 0, '2024-03-24 11:12:12', '2024-03-24 11:12:12'),
(1531, 1, 1, 22, 57, 'KOХA февруари 2024', 'koha-fevruari-2024', '', '', '', 'koha2024.02.jpg', 0, 0, 1, 0, '2024-02-23 11:12:12', '2024-02-23 11:12:12'),
(1532, 1, 1, 22, 57, 'KOХA март 2024', 'koha-mart-2024', '', '', '', 'koha2024.03.jpg', 0, 0, 1, 0, '2024-03-23 11:12:12', '2024-03-23 11:12:12'),
(1533, 1, 1, 8, 57, 'Eднакви можности на жените и мажите', 'ednakvi-mozhnosti-na-zhenite-i-mazhite', '', 'Не случајно многу земји НОВАТА година ја слават во март особено земјите каде владее источната мудрост. Можеби затоа и народот кај нас на месец март му дал „женско име“ – „Баба Марта“.  А додатокот „баба“ асоцира на мудрост која ја имаат возрасните.', '<p>Не случајно  многу земји НОВАТА година ја слават во март особено земјите каде владее  источната мудрост. Можеби затоа и народот кај нас на месец март му дал &bdquo;женско  име&ldquo; – &bdquo;Баба Марта&ldquo;.  А додатокот &bdquo;баба&ldquo;  асоцира на мудрост која ја имаат возрасните.</p>\r\n<p>Месец март по  некое непишано правило е месец на жените и не само заради тоа што во месец март  го одбележуваме 8 март, Меѓународниот ден на жената, туки заради и други факти  што се случуваат во овој месец како што е будење на природата и почеток на нов  живот во едногодишниот циклус на природата. И кога сме во &bdquo;месецот на жените&ldquo;  збор два за местото на жените во нашето општество.</p>\r\n<p>Рамноправноста меѓу  половите е една од темелните вредности на Уставот на Република Македонија која се  заснива на целосно признавање и промовирање на еднаквите можности на жените  и мажите.</p>\r\n<p>Еднаквите можности  меѓу мажите и жените во Република Македонија се регулирани со Уставот како  највисок правен акт, но во голем број закони се внесени одредби со кои се забранува  дискриминација врз основа на пол и одредби со кои се инкорпорира родовиот концепт  во домашната законска регулатива. Иако, во значителен број  закони е вграден родовиот концепт, се до 2006 година  не беше донесен посебен закон со кој се промовираат и унапредуваат  еднаквите можности на мажите и жените. Имено, во 2006 година, Собранието  на РМ го донесе Законот за еднакви можности на жените и мажжите, кој во 2008 година беше  усогласен со постојните дефиниции за дискриминација и видовите на дискриминација  со Директивите на Европската Унија, кои го регулираат  нееднаквиот третман на жените и мажите. Во Сл. весник на РМ  бр 6/2012 год. е објавен најновиот текст на овој закон. </p>\r\n<p>Според тоа, Законот  за еднакви можности на жените и мажите е важен инструмент  за обезбедување на еднаков третман на жените и мажите во сите  сфери на нашето општество. Имлементацијата на овој општ документ  се одвива и преку подзаконските акти кои согласно принципот на хиерархија  на законите, подзаконските акти треба да го спроведуваат  законот, но не треба да го надминуваат.</p>\r\n<p>Како што е познато  субјекти одговорни за следење и примена на мерките насочени  кон воспоставување на еднакви можности е Министерството за труд и  социјална политика, кое ја има водечката улога во однос  на родовата переспектива и унапредување на еднаквите  можности. Од ова министерство се бараат мислења за сите материјали доставени  до Владата на РМ, кои се однесуваат на родовата еднаквост.</p>\r\n<p>Заради статусот  на Р. Македонија како кандидат за Европската Унија очигледна  е потребата од поголема конзистентност на овој дел на законодавство  со стандардите на ЕУ. Познато е дека споменатиот  закон се спроведува преку три вида мерки. </p>\r\n<p><strong>Прво</strong>, позитивните мерки или мерки на позитивна  дискриминација кои првенствено се однесуваат на воспоставување еднаква застапеност  на мажите и жените во јавниот и политичкиот живот и во тој правец  се предвидуваат пиоритети на соодветниот пол се до воспоствување  еднакво учество на жените и мажите. Нееднакво учество постои кога застапеноста  на било кој од половите е на ниво помало од 40%. </p>\r\n<p><strong>Второ</strong>, мерки кои предвидуваат различни  активности кои треба да ги поттикнат слободите и  напорите за елиминирање на еднаква застапеност, односно нееднаквиот третман.</p>\r\n<p><strong>Трето</strong>, подигањето на свеста со цел  поттикнување, воведување и унапредување на принципот  на еднакви можности на мажите и жените, што е од  голема важност. Заради воведување на принципот на &quot;еднакви можности&rdquo; за  жените и мажите се предвидува обврска на органите на централната и  локалната власт за активна соработка со работодавци, синдикатот, релевантни невладини  организации и здруженија на граѓани.</p>\r\n<p>Согласно Законот  за еднакви можности на жените и мажите, донесен е и Националниот план за акција  за постигнување на еднакви можности на жените и мажите. Тој претставува  базичен документ и е симбиоза на сите најразновидни планирани и координирани мерки  и активности што треба да бидат преземени и спроведени  во разни области од општештвеното живеење во насока  на воспоствување на принципот на еднакви можности.</p>\r\n<p>За обезбедување  транспарентност на целокупниот процес, Законот предвидува обврска Владата да  изготвува Извештај за спроведување на мерки и активности од Планот  за акција еднаш годишно и тој да биде достапен до  јавноста.</p>\r\n<p>Анализата на примената  на Законот за еднакви можности на жените и мажите, достапна на јавноста, и покрај  големото значење, покажува дека сензибилноста кон ова прашање е се&rsquo;  уште на ниско ниво. Она што е сега потребно е воспоставувањето механизам за  инкорпорирање на родовиот аспект во законите, програмите  и проектите и на локално ниво.</p>\r\n<p>Имајќи го предвид  изложеното, со цел промоција и поттикнување на прашањето на &quot;еднакви можности&ldquo;  принципот на еднаквите можности се гледа во членот 21 од Законот  за здруженија и фондации каде е утврдено дека со здруженијата  управуваат членовите непосредно преку избраните претставници  во органите на здружението.</p>\r\n<p>Ако во нашата држава  постојат здруженија на граѓани кои се борат за еднаквост на жените и  мажите, тогаш неопходно е во Законот за пензионерско организирање (кој пред  три-четири години е доставен до Министерството  за труд и социјална политика) да се вгради и родовиот аспект, усогласувајќи  го истиот со европските директиви.</p>\r\n<p>И на крајот  како додаток на сето ова:</p>\r\n<p>Во Сојузот на здруженија на пензионери на Македонија  по 78 години постоење оваа 2024  година го дочекавме 8. Март со 12 претседателки од 53 здруженијана пензионери  или во проценти 22%, за разлика од пред 20 години кога имало само 4  претседателки на здруженија или само 8%. При тоа сите здруженија на кои  претседателка е жена нижат успеси и резултати. </p>\r\n<p>Зошто овие 12  претседателки се нафатиле да бидат на чело на едно здружение и покрај големата  одговорност и потрба да се справат со повеќе предизви, на сите главна мотивација да ја прифатам оваа  функција им била да се зголемат активностите на секој план, да бидат опфатени  што поголем број на жени (да бидат вклучени и во донесување одлуки) и да се  збогати пензионерскиот живот со нови активности и форми. Исто така, присутна  била желбата  да ја подигнат свеста за  равноправност меѓу жените и мажите, свеста за чување на здравјето и примена на  здрава исхрана на жените, но и на сите членови на оваа најмногубројна  популација, како и заштита од семејно и друго насилство и друго. Во сите овие  здруженија и во Сојузот резултатите се видливи! </p>\r\n<p>Станка Трајкова<br>\r\nПретседател на Сојузот  на здруженија на пензионери на Македонија</p>', 'Stanka2024.png', 0, 0, 1, 0, '2024-03-26 11:12:12', '2024-03-26 11:12:12'),
(1534, 1, 1, 8, 57, 'Сојузот води грижа за здравјето на членството', 'sojuzot-vodi-grizha-za-zdravjeto-na-chlenstvoto', '', 'Промедика и Систина Офтамологија ќе вршат прегледи на пензионери од СП Бутел на 25 април 2024', '<a href=\"/vesnik/images/info/Soopstenie za Promedika i Oftamologija.jpg\" target=\"_blank\"><img src=\"/vesnik/images/info/Soopstenie za Promedika i Oftamologija.jpg\"></a>', '', 0, 0, 1, 0, '2024-04-19 10:12:12', '2024-04-19 10:12:12'),
(1535, 1, 1, 22, 57, 'KOХA април 2024', 'koha-april-2024', '', '', '', 'koha2024.04.jpg', 0, 0, 1, 0, '2024-04-21 10:12:12', '2024-04-21 10:12:12'),
(1536, 1, 1, 24, 57, '188', '188', '', '', '', '188.jpg', 0, 0, 1, 0, '2024-04-21 10:12:12', '2024-04-21 10:12:12'),
(1537, 1, 1, 8, 57, 'За и околу регионалните и републичката ревија на песни, музика и игри и спортските натпревари во организација на СЗПМ', 'za-i-okolu-regionalnite-i-republichkata-revija-na-pesni-muzika-i-igri-i-sportskite-natprevari-vo-organizacija-na-szpm', '', 'Регионалните и Републичката ревија на песни, музика и игри и спортските регионални и републички натпревари се најмасовните традиционални и манифестации кои најдолго се одржуваат во организација на СЗПМ', '<p>Регионалните  и Републичката ревија на песни, музика и игри и спортските  регионални и републички натпревари се најмасовните  традиционални и манифестации кои најдолго се одржуваат во организација на Сојузот  на здруженијата на пензионерите на Македонија, на кои учествуваат повеќе од 25  000 пензионери организирани во 53 здруженија членки на Сојузот.</p>\r\n<p>Целта на овие  значајни активности кои се одржуваат секоја година е подобар и посодржаен живот  на луѓето од третата доба и претставува програмска определба на Сојузот. Регионалните  манифестации се одржуваат секоја година во други 8 градови со исклучителна согласност  на одбраното здружение, кое може да се откаже од оправдани или субјективни причини  и тогаш на негово место се кооптира друго здружение кое може и сака да биде домаќин.  При изборот на здружение не се гледа големината на здружението туку неговите можности  и ред да биде домаќин. Да бидат домаќини сите здруженија прифаќаат со радост И тоа  им претставува чест. Домаќини биле и Вевчани и Лозово и Дојран, но и Битола, Прилеп,  Куманово со членови од 350 до 19 000 членови. Со други зборови изборот не е препуштен  на случајност. Активностите се одржуваат според правилници и финансиска и секаква  друга помош од Сојузот. Ревиите имаат главно ревијален карактер, но и извесен натпреварувачки  карактер. Изборот го врши Комисијата за културно-забавен живот при ИО на СЗПМ и  Организацискиот одбор, на предлог на селекторот. Најдоброто здружение или најдобрите  две се стекнуваат со право да учествуваат на Меѓународниот фестивал на трета доба  во Љубљана каде го претставуваат Сојузот, градот и нашата држава, па затоа многу  се внимава при изборот на претставникот. Токму заради оваа манифестација, ревиите  на песни, музика и игри, Сојузот доби статус на организација од јавен интерес со  решение на Владата на РСМ.</p>\r\n<p>Спортските  регионални и републички натпревари имаат исклучително натпреварувачки карактер.  Резултатите ги верифицираат стручни луѓе - пензионери со големо искуство и знаење  кај спортските натпревари, таканаречени судии, како и Организациски одбор и Комисијата  за спорт и рекреација при ИО на СЗПМ.</p>\r\n<p>По сите овие  добро и квалитетно организирани активности сме препознатливи и во опкружувањето  на нашата земја, затоа сите недобронамерни инсинуации не му користат на никој, а  најмалку на некои &bdquo;автори&ldquo; што се обидуваат да ја намалат важноста на оваа манифестација.  За таа цел нека придонесат тие што критикуваат на дело, а не на збор без факти.</p>\r\n<img src=\"/vesnik/images/info/revija.jpg\"/>\r\n<p>Со заеднички  сили секогаш ќе бидеме подобри и поквалитетни во сите активности, а особено на овие  кои ги изведуваат пензионери аматери со голем ентузијазам и елан. На крајот во практика  се одржуваат подготвителни состаноци кои треба да бидат исклучително со работен  карактер. Во прилог на подготвителните состаноци и воопшто во реализирање на регионалните  и републички манифестации здруженијата треба да се придржуваат до членот 11 и  12 од Одлуката за утврдување на единствените критериуми за материјално-финансиското  работење на здруженијата на пензионери и Сојузот на здруженијата на пензионерите  на Македонија кои одредби гласат:</p>\r\n<p>Член 11</p>\r\n<p>Финансирањето  на реализацијата на заеднички манифестации или манифестации од посебен интерес од  спортот и културата, здруженијата го вршат од сопствени средства, од средства добиени  од покровители и од Сојузот (за домаќините на регионалните и републичките спортски  натпревари, на домаќините на регионалните и републичката ревија или за одредени  проекти). </p>\r\n<p>Висината на  средствата за намената од став 1 на овој член, здруженијата и Сојузот ги утврдуваат  со своите финансиски планови и со пресметките за програмите, за секој проект одделно.</p>\r\n<p>Член 12</p>\r\n<p>Поединечниот  износ за здруженија домаќини на регионалните и републичките спортски игри, за регионалните  и републичката ревија на песна, музика и игра или посебни проекти во областа на  културата, се определува за покривање на следните позиции: за домаќините на регионалните  спортски игри до износот на реалните трошоци според претходно утврдена посебна пресметка  на трошоци која опфаќа трошоци на уредување на терените, озвучување на сцената за  отворање и затворање на игрите, осигурување на учесниците во атлетските дисциплини,  трошоци за лекарска екипа и друго; за домаќините на регионалните и републичката  ревија на песна, музика и игра, до износот на реалните трошоци според претходно  утврдена посебна пресметка на трошоци на закуп на сала, озвучување на сцената за  отворање и затворање на ревијата и трошоци за селекторот и друго. Надлежниот орган  на Сојузот со посебен акт, во рамки на годишната програма за работа и финансискиот  план ги определува домаќините на регионалните и републичките манифестации и организаторите  на посебни проекти.&ldquo; </p>', 'sportski natprevari.jpg', 0, 0, 1, 0, '2024-04-28 10:12:12', '2024-04-28 10:12:12'),
(1538, 1, 1, 8, 57, 'Учесници на 14. Републичка ревија на песни, музика и игри', 'uchesnici-na-14-republichka-revija-na-pesni-muzika-i-igri', 'Комисија за културно-забавен живот при СЗПМ', 'Здруженија учесници на 14. Републичка ревија на песни, музика и игри која ќе се одржи на 8 мај 2024 година во Домот на АРМ во Скопје', '<p>Организацискиот  одбор при СЗПМ и на  предлог на селекторот Томче Стојков, на проширена седница на Комисијата за  културно-забавен живот при ИО на СЗПМ на која присуствуваа Претседателката на  Сојузот Станка Трајкова, потпретседателот на ИО на СЗПМ Ментор Ќоку и  секретарот Менка Темелковска ја донесе листата на здруженијата кои ќе земат  учество на 14-тата републичка ревија на песни, музика и игри на 8 јуни 2024  година во Домот на АРМ во Скопје.</p>\r\n<p>На 14-тата Републичката  ревија на песни, музика и игри ќе настапат 26 здруженија на пензионери и тоа:  </p>\r\n<ol>\r\n    <li>ЗП Центар - хор „Садила Јана“ и „Сношти минав, мила мамо“ </li>\r\n    <li>ЗП Кавадарци - пејачка група „Тиквешија плодна“</li>\r\n    <li>ЗП Гостивар – соло-изведба „Запеј Македонијо“ и албанска група со песната „Суле бег“</li>\r\n    <li>ЗП Охрид и Дебрца - сплет на ора „Деветорка“, „Чамчето“ и „Мешано старо оро“</li>\r\n    <li>ЗП Крушево – пејачка група со влашка песна „Ми скулај уна тахина“</li>\r\n    <li>ЗП Битола - пејачка група „Сирма Војвода“ со песната „Ајде да бегаме“ </li>\r\n    <li>ЗП Македонска Каменица - женско трио со песната „Ниша се ниша Неда“ заедно со орото „Копачка“</li>\r\n    <li>ЗП Гевгелија - перформанс соло-песна „Софка на татко“</li>\r\n    <li>ЗП Лозово – обичај „Замесок“</li>\r\n    <li>ЗП Куманово - сплет на песна и оро „Сле се фана“</li>\r\n    <li>ЗП Тетово – со песната „Кажи ми кажи облаче бело“, а албанска група ќе изведе албански свадбен обичај</li>\r\n    <li>ЗП Лабуништа - поет-хуморист Шабан Адемоски</li>\r\n    <li>ЗП Неготино – пејачка група со песната „Што си налутено либе“</li>\r\n    <li>ЗП ОВР на РМ – фолклорна група со орото „Каламатија“ </li>\r\n    <li>ЗП Кочани – сплет од две ора „Арнаут“ и „Елено моме“</li>\r\n    <li>ЗП Прилеп – пејачка група „Пилето ми пее рано на сабајле“</li>\r\n    <li>ЗП Солидарност - Аеродром – фолклорна група со песната „Прстен ми падна мале“</li>\r\n    <li>ЗП Берово – пејачка група со песната „Маго, Маго, вакло пиле“</li>\r\n    <li>ЗП Демир Хисар – фолклорна група со орото „Касапско“</li>\r\n    <li>ЗП Свети Николе – пејачка група со песната „Девојченце, девојченце љубиш ли ме“</li>\r\n    <li>ЗП Бутел – пејачка група со песната „Јовано Јованке“</li>\r\n    <li>ЗП Радовиш и Конче – сплет од две ора</li>\r\n    <li>ЗП Кисела Вода – фолклорна група со орото „Пуштено оро“</li>\r\n    <li>ЗП Ѓорче Петров – фолклорна група со сплет на „Ангелино моме“ и „Свекрвино оро“</li>\r\n    <li>ЗП Гази Баба – пејачка група со песната „Нема друга Македонија“</li>\r\n    <li>ЗП Шуто Оризари – сплет на ора.</li>\r\n</ol>\r\n<p>&nbsp;</p>', '', 0, 0, 1, 0, '2024-05-23 10:12:12', '2024-05-23 10:12:12'),
(1539, 1, 1, 8, 57, 'Потпишан Меморандум за соработка и учество на Сојузот во проектот City&Co', 'potpishan-memorandum-za-sorabotka-i-uchestvo-na-sojuzot-vo-proektot-cityco', 'Работна средба во Сојузот на здруженија на пензионери на Македонија', 'На 14.05.2024 година во просториите на Сојузот на здруженија на пензионери на Македонија, се одржа работна средба во рамки на соработката помеѓу Универзитетот „Мајка Тереза“ во Скопје и Универзитетот за применети науки во Хаг, Холандија', '<p>На 14.05.2024 година во  просториите на Сојузот на здруженија на пензионери на Македонија, се одржа  работна средба во рамки на соработката помеѓу Универзитетот &bdquo;Мајка Тереза&ldquo; во  Скопје и Универзитетот за применети науки во Хаг, Холандија. Двата универзитети  заеднички работат на испитување на ставовите на старите лица за тоа дали  животот во Скопје е приспособен на потребите на лицата на возраст над 65  години. Со цел добивање на подетални информации за состојбите на локално,  односно микро ниво, во моментот се реализира активноста за фотопродукција со  употреба на геопортал. Оваа активност се реализира во рамки на проектот  City&amp;Co. Во активностите за собирање на информации за состојбите во градот  Скопје активно се вклучени членови на Сојуз на здруженија на пензионери на  Македонија.</p>\r\n<p>На работната средба беше потпишан  Меморандум за соработка помеѓу Сојузот и Универзитетот &bdquo;Мајка Тереза&ldquo; во Скопје.  Потписници на Меморандумот се Вон. Проф Дитурије Исмаили, декан на Факултетот  за социјални науки и Станка Трајкова, претседателка на Сојуз на здруженија на  пензионери на Македонија.</p>\r\n<p>Меморандумот го содржи следното:</p>\r\n<ul type=\"disc\">\r\n    <li>Соработка во организирање на конференции, семинари, работилници и       тркалезни маси од национален и меѓународен карактер;</li>\r\n    <li>Организирање заеднички настани и активности;</li>\r\n    <li>Заедничко изготвување и аплицирање на проекти од заеднички интерес;</li>\r\n    <li>Заедничко изготвување на анализи, стручни и научни мислења и       истражувања и размена на информации заради заемни потреби;</li>\r\n    <li>Обезбедување на заемна поддршка за прашања од заеднички интерес;</li>\r\n    <li>Споделување на позитивни практики и искуства;</li>\r\n    <li>Проширување на мрежата на соработка со други институции и организации;</li>\r\n    <li>Меѓусебно консултирање и информирање за прашања од интерес на старите       лица;</li>\r\n    <li>Други области на кои ќе се согласат потписниците.</li>\r\n</ul>\r\n<p>На работната средба на присутните  им се обратија претседателката на Сојузот Станка Трајкова, Вон. Проф. Даниел  Павловски, продекан за наука на факултетот за социјални науки при Универзитетот  &bdquo;Мајка Тереза&ldquo; во Скопје и проф. Joost van Hoof професор на Универзитетот за  применети науки во Хаг, Холандија и директор на центарот за урбано стареење,  кој ги поздрави присутните преку видео врска и го објасни значењето, целта и  користа од проектот City&amp;Co.</p>\r\n<p>Потоа Проф. Даниел Павловски на  присутните им го објасни начинот на користење на електронските алатки за  собирање на податоци и сите чекори кои учесниците треба да ги направат при  испраќање на материјали.</p>\r\n<p align=\"center\"><img src=\"/vesnik/images/info/city and co.jpg\"></p>\r\n<p>По презентацијата имаше прашања  од присутните на кои Проф. Павловски исцрпно одговараше. На средбата беше  договорено да се формира група на &bdquo;Viber&ldquo; за поедноставна и подобра  комуникација.</p>\r\n<p>На крајот од средбата присутните членови  на сојузот истакнаа дека очекуваат со овој проект да се соберат корисни  инфорамции кои ќе можат да бидат искористени за подобрување на состојбите во  градот Скопје. Според нив благодарение на Проф. Даниел Павловски и неговата  умешност, непосредност и елоквентност презентацијата беше одлична.</p>', 'city and co (2).jpg', 0, 0, 1, 0, '2024-05-15 10:12:12', '2024-05-15 10:12:12'),
(1540, 1, 1, 24, 57, '189', '189', '', '', '', '189.jpg', 0, 0, 1, 0, '2024-05-23 10:12:12', '2024-05-23 10:12:12'),
(1541, 1, 1, 22, 57, 'KOХA мај 2024', 'koha-maj-2024', '', '', '', 'koha2024.05.jpg', 0, 0, 1, 0, '2024-05-30 10:12:12', '2024-05-30 10:12:12'),
(1542, 1, 1, 8, 57, 'Оглас за финансиска поддршка за поттикнување издавачка дејност и творештво', 'oglas-za-finansiska-poddrshka-za-pottiknuvanje-izdavachka-dejnost-i-tvoreshtvo', '', 'На седницата одржана на 25.6.2024 година, ИО на СЗПМ донесе одлука за финансиска поддршка за поттикнување издавачка дејност и творештво, во согласност со Програмата и Финансискиот план за 2024 година', '<p>На седницата одржана на 25.6.2024 година, ИО на СЗПМ донесе одлука за финансиска  поддршка за поттикнување издавачка дејност и творештво, во согласност со Програмата  и Финансискиот план за 2024 година на Сојузот на здруженија на пензионери на Македонија.  Според Правилникот на Сојузот за оваа активност: </p>\r\n<ul>\r\n    <li> На огласот можат да се јават поединци, групи  и здруженија. </li>\r\n    <li> Може да се конкурира за: издавање на книги, концерти,  театарски претстави, изложби, литературни митинзи и друго. </li>\r\n    <li> Пријавите се земаат во СЗПМ или можат да се симнат од <a href=\"/vesnik/images/info/OGLAS%20ZA%20IZDAVASTVO%20A4.jpg\" target=\"_blank\">веб-страницата на СЗПМ</a>. </li>\r\n    <li> Пријавите треба да се потпишани од претседателот  на здружението. </li>\r\n    <li> Може да се конкурира само за еден проект,  а приоритет }е имаат автори и здруженија кои не  добиле досега средства од Сојузот и дела и проекти со содржина поврзана со третата  доба. </li>\r\n    <li>Материјалите треба да бидат доставени во архивата  на СЗПМ или по пошта на адреса:</li>\r\n</ul>\r\n<p><strong>Сојуз на здруженија  на пензионери на Македонија</strong></p>\r\n<p><strong>Булевар &quot;Кочо  Рацин&quot; бр. 14, 1000 Скопје, П. Фах 440</strong></p>\r\n<p><strong>&quot;За огласот  за поттикнување на издавачка дејност и творештво за 2024 година на СЗПМ&quot;. </strong></p>\r\n<p> Нема да бидат разгледувани пријави: доставени по истекот на рокот на Огласот,  нецелосно пополнети и некомплетни пријави без приложена документација која задолжително  се бара во пријавата. Материјалите доставени на Огласот не се враќаат.</p>\r\n<p><strong>Огласот трае од 1 до 20 јули 2024 година, а резултатите  ќе бидат објавени во весникот &quot;Пензионер плус&quot; и на веб-страницата на  СЗПМ.</strong></p>\r\n<p align=\"right\"><strong>ИО на СЗПМ</strong></p>\r\n<a href=\"/vesnik/images/info/OGLAS%20ZA%20IZDAVASTVO%20A4.jpg\" target=\"_blank\"><img src=\"/vesnik/images/info/OGLAS%20ZA%20IZDAVASTVO%20A4.jpg\"/></a>', '', 0, 0, 1, 0, '2024-06-18 10:12:12', '2024-06-18 10:12:12'),
(1543, 1, 1, 8, 57, 'Емисија „Трета доба“ на МТВ', 'emisija-treta-doba-na-mtv', '', 'На 1 јуни 2024 година претседателката на СЗПМ Станка Трајкова гостуваше во емисијата „Трета доба“ на МТВ', '<p>Емисијата &bdquo;Трета  доба&ldquo; која се емитува на првата програма на Македонската телевизија, премиерно секоја прва сабота во 13:30 часот, по  вестите, и репризно наредната сабота, од ден на ден е сè попопуларна меѓу  пензионерите заради интересните содржини и убавото водење и непосредната  комуникација со учесниците во емисијата, на новинарката и  водителка Јасмина Никова.  Во емисијата  има песни, музика, спорт, хуманитарни и други активности на здруженијата и на  Сојузот. Честопати во емисијата гостуваат и претставници на раководствата на  здруженијата и на Сојузот. </p>\r\n<p>На 1 јуни 2024 година во емисијата гостин беше Претседателката на Сојузот  на здруженијата на пензионерите на Македонија Станка Трајкова, која даде  конкретен и автентичен пресек на активностите на Сојузот во изминатиот период.  Таа меѓу другото рече:</p>\r\n<p align=\"center\"><img src=\"/vesnik/images/info/treto doba 1.gif\"></p>\r\n<p>-Дозволете ми да започнам со зборовите: &bdquo;Мај, е месец на песна, музика и игри&ldquo;, таков имаше текст на насловната страна на нашиот весник &bdquo;Пензионер  плус&ldquo;. Тоа кореспондира со реалноста бидејќи почнувајќи од 17 април,  каде што домаќин беше ЗП Битола, започнаа  регионалните ревии на песни, музика и игри, а осмата и последна регионална  ревија беше на 21 мај, каде што домаќин беше ЗП Солидарност-Аеродром.</p>\r\n<p>Како што е познато тоа се наши традиционални активности кои се случуваат по  22 пат. Одржувањето на регионалните ревии кои се одржуваат во 8 региони со  право го нарекуваме пролетно музичко движење или музички караван кој  има и во себе друга компонента, а тоа е  дружењето на пензионерите.  </p>\r\n<p>Пред извесно време  прочитав дека осаменоста&nbsp; во Германија ја опишуваат како епидемија  поврзувајќи ја со прашалната реченица: &rdquo;Дали осаменоста не е закана и за демократијата?&ldquo;  &nbsp;Затоа треба да ги повикуваме пензионерите да се вклучат &nbsp;во  активностите на здуженијата или да присуствуваат на настани кои &nbsp;се бесплатини и достапни на сите. Затоа  што една средба со пријател или прашањето &bdquo;КАКО СИ?&ldquo; допринесува за миг да  заборавите на осаменоста. Отука, не &nbsp;е случајно &nbsp;нашите колеги  пензионери ги создале регионалните и републичката ревија на песни, музика и  игри кои со право се вклопуваат во мудрата мисла дека &nbsp;она што можеш ти да го направиш, неможам јас, но заедно  можеме. </p>\r\n<p align=\"center\"><img src=\"/vesnik/images/info/treto doba 2.gif\"></p>\r\n<p>Радува фактот што од година на година овие регионални ревии на песни,  музика и игри се сè помасовни и поквалитетни. Во регионалните ревии зедоа  учество околу 1 000 пензионери.  Кога сме  кај ревиите да информирам дека Републичката ревија на песни музика и игри по  14-ти пат ќе се одржи на 8 јуни 2024 година во Домот на АРМ, за што сме му  посебно благодарни на Министерството за одбрана.  </p>\r\n<p>Покрај активностите на културно-забавен план, на 19 мај започнаа  да се реализираат и традиционалните регионални  спортски натпревари кои започнаа во Пехчево, а ќе завршат на 22 мај 2024 година  во Богданци.</p>\r\n<p>Паралелно со одвивање на културните и   спортските активности се остваруваат и други активности.  Така  на 14.5.2024 година во просториите на Сојузот  на здруженијата на пензионерите на Македонија, се одржа работна средба во рамки  на соработката помеѓу Универзитетот &bdquo;Мајка Тереза&ldquo; во Скопје и Универзитетот за  применети науки во Хаг, Холандија. Двата универзитета заеднички работат на  испитување на ставовите на старите лица за тоа дали животот во Скопје е  приспособен на потребите на лицата на возраст над 65 години. Со цел добивање на  подетални информации за состојбите на локално, односно микро ниво, во моментот  се реализира активноста за фотопродукција со употреба на геопортал. Оваа  активност се реализира во рамки на проектот City&amp;Co. Во активностите за  собирање информации за состојбите во градот Скопје активно се вклучени членови  на Сојузот.  На работната средба беше  потпишан Меморандум за соработка помеѓу Сојузот и Универзитетот &bdquo;Мајка Тереза&ldquo;  во Скопје. Потоа проф. Даниел Павловски на присутните им го објасни начинот на  користење на електронските алатки за собирање податоци и сите чекори кои  учесниците треба да ги направат при испраќање на материјали. Според нив  благодарение на проф. Даниел Павловски и неговата умешност, непосредност и  елоквентност презентацијата беше одлична.</p>\r\n<p>Се разбира дека  имавме и други активности, но овие се најважните во овој период.</p>\r\n<p>Сите емисии на  &bdquo;Трета доба&ldquo; која се емитува на првата програма на Македонската телевизија може да ги гледате на следниот  линк: <a href=\"https://play.mrt.com.mk/c/1118\" target=\"_blank\">https://play.mrt.com.mk/c/1118</a></p>', 'treto doba 3.jpg', 0, 0, 1, 0, '2024-06-04 10:12:12', '2024-06-04 10:12:12'),
