(1800, 1, 1, 10, 30, 'Организација', 'organizacija', NULL, NULL, '<table width=\"100%\" class=\"dijagram\">\r\n              <tr>\r\n                <td colspan=\"3\">Собрание</td>\r\n              </tr>\r\n              <tr>\r\n                <td width=\"33%\">Извршен одбор</td>\r\n                <td width=\"33%\">Надзорен одбор</td>\r\n                <td width=\"33%\">Актив на пензионерки</td>\r\n              </tr>\r\n            </table>\r\n            <table width=\"100%\" class=\"dijagram\">\r\n              <tr>\r\n                <td width=\"33%\">Комисија  за статут  и правни прашања </td>\r\n                <td width=\"33%\">Комисија за спорт  и рекреација</td>\r\n                <td width=\"33%\">Комисија  за културно-  забавен живот</td>\r\n              </tr>\r\n              <tr>\r\n                <td>Комисија  за здравство  социјална заштита</td>\r\n                <td>Комисија  за стамбени  прашања и деловен простор </td>\r\n              </tr>\r\n            </table>\r\n            <p>Ограноци<br />\r\n              Крушево 1, Крушево 2, с.Житоше, с.Св.Митрани, с. Пуста Река</p>\r\n            <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" class=\"bez_padding\">\r\n            <tr>\r\n              <td width=\"32%\" align=\"center\"><img src=\"Nikola Atanasoski pretsedatel na Sobranieto.JPG\" alt=\"\" width=\"139\" height=\"195\" /></td>\r\n              <td width=\"1%\" align=\"center\"></td>\r\n              <td width=\"32%\" align=\"center\"><img src=\"Milorad-Trpcheski-pretsedatel-na-Sobranie-.jpg\" alt=\"\" width=\"142\" height=\"189\" /></td>\r\n              <td width=\"1%\" align=\"center\">&nbsp;</td>\r\n              <td width=\"32%\" align=\"center\"><img src=\"Dimche-Trajkoski-sekretar-na-ZP-Krushevo.jpg\" alt=\"\" width=\"142\" height=\"189\" /></td>\r\n              </tr>\r\n            <tr>\r\n              <td align=\"center\" valign=\"top\"><strong><em>Никола Атанасоски</em></strong>, <br />\r\n                Претседател на ЗП Крушево</td>\r\n              <td align=\"center\" valign=\"top\"></td>\r\n              <td align=\"center\" valign=\"top\"><strong><em>Милорад Трпчески</em></strong>, <br />\r\n                Претседател  на собрание </td>\r\n              <td align=\"center\" valign=\"top\">&nbsp;</td>\r\n              <td align=\"center\" valign=\"top\"><strong><em>Димче Трајкоски</em></strong>, <br />\r\n                Секретар на ЗП Крушево</td>\r\n              </tr>\r\n          </table> \r\n            <p align=\"center\"><img src=\"Krusevo-Sobranie-0002.jpg\" alt=\"\" /><br />\r\n            <strong><em>Собрание на Здружението</em></strong><br /></p>\r\n            <h3><strong>С О Б Р А Н И Е</strong> </h3>\r\n            <ol start=\"1\" type=\"1\">\r\n              <li>Милорад Трпчески&nbsp; - претседател</li>\r\n              <li>Димче Трајкоски&nbsp;&nbsp;&nbsp;&nbsp; - секретар</li>\r\n              <li>Никола Атанасоски &ndash; член</li>\r\n              <li>Петар Начески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Илија Дамчески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Димитар Јованоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Добринка Јованоска&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Елена Чавкароска&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Петар Талески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Нико Курета&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Петар Котески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Грујо Огненоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Благоја Атанасоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Василика Дамјаноска&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Натка Мишу&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Никола Иваноски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Киро Секулоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Џемаил Јусуфи&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Иљаз Спахич&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Киро Ѓорѓиоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n              <li>Раде Тошески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</li>\r\n            </ol>\r\n            <p> </p>\r\n          <p align=\"center\"><img src=\"Krusevo-IO-0001.jpg\" alt=\"\" /><br />\r\n            <strong><em>Извршен одбор</em></strong></p>\r\n            <h3><strong>ИЗВРШЕН ОДБОР</strong></h3>\r\n            <p>1. Никола Атанасоски&nbsp;&nbsp; - претседател<br>2. Димче Трајкоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - секретар<br>3. Петар Начески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - член<br>4. Петар Котески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>5. Петар Талески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>6. Василика Дамјаноска&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>7. Добринка Јованоска&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>8. Грујо Огненоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>9. Нико Курета&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>НАДЗОРЕН ОДБОР</h3>\r\n            <p>1. Ставре Хасаноски &nbsp;&nbsp;- претседател<br>2. Илка Дервенџи &ndash; член<br>3. Ванѓел Златески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>Комисија за статут и правни прашања </h3>\r\n            <p>1. Никола Атанасоски &nbsp;&nbsp;&nbsp;&nbsp;- претседател<br>2. Милорад Трпчески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - член<br>3. Димче Трајкоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>4. Киро Ѓорѓиоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>5. Џемаил Јусуфи&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>Комисија за спорт и рекреација </h3>\r\n            <p>1. Димитар  Костоски&nbsp;&nbsp;&nbsp; - претседател<br>2. Таќи  Папакоча&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; член<br>3. Миха  Божиноски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>4. Жаклина  Николоска&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>5. Петар  Ристески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>Комисија за културно-забавен живот </h3>\r\n            <p>1. Петар  Котески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - претседател<br>2. Василика  Дамјаноска&nbsp;&nbsp; член<br>3. Митко  Илиески&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&ldquo;</p>\r\n            <h3>Комисија за здравство и социјална заштита </h3>\r\n            <p>1. Д-р&nbsp; Ѓорѓи Тирчоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - претседател<br>2. Д-р&nbsp; Савка Христоска&nbsp;&nbsp;&nbsp; - член<br>3. Драган  Милошески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp;&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>Комисија за стамбени прашања и деловен простор</h3>\r\n            <p>1. Нико  Курета&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - претседател<br>2. Димче  Трајкоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; член<br>3. Натка  Мишу&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>ОДБОР НА ОГРАНОКОТ 1  КРУШЕВО</h3>\r\n            <p>1. Петар  Талески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - претседател<br>2. Кочо Гута&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - секретар<br>3. Стојан  Стеваноски &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- член<br>4. Еленица  Преш&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>5. Христо  Илиески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>КОМИСИЈА ЗА СПОРТ И  КУЛТУРНИ ДЕЈНОСТИ</h3>\r\n            <p>1. Миха  Божиноски&nbsp;&nbsp;&nbsp;&nbsp; - претседател<br>2. Михајло  Габелоски &ndash; секретар<br>3. Димитар  Костоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; член<br>4. Наум  Петрушески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;<br>\r\n            5. Марија Дамческа&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>ОДБОР НА ОГРАНОКОТ 2  КРУШЕВО</h3>\r\n            <p>1. Петар  Котески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - претседател<br>2. Калиопа  Наумоска&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - секретар<br>3. Томе  Петроски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp; член<br>4. Благородна  Јуца&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp;&nbsp; &ldquo;<br>5. Ангеле  Ангелески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>КОМИСИЈА ЗА СПОРТ И  КУЛТУРНИ ДЕЈНОСТИ</h3>\r\n            <p>1. Таќи  Папакоча&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - претседател<br>2. Аница  Налбанти&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - секретар<br>3. Христо  Петрески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; член<br>4. Жаклина  Николоска&nbsp;&nbsp; &ldquo;<br />\r\n            5. Ванѓел Пажик&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;            </p>\r\n            <h3>ОДБОР НА ОГРАНОКОТ СВЕТО МИТРАНИ</h3>\r\n            <p>1. Киро  Ѓорѓиоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - претседател<br>2. Раде  Тошески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - секретар<br>3. Митан  Митаноски &ndash; член<br>4. Малим  Алимоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>5. Данчо  Србиноски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>КОМИСИЈА ЗА СПОРТ И  КУЛТУРНИ ДЕЈНОСTИ</h3>\r\n            <p>1. Трајче  Тодороски&nbsp;&nbsp; - претседател<br>2. Ѓорѓи  Богески&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - секретар<br>3. Димче  Митрески&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;член<br>4. Сабри  Зекироски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>5. Борис  Јанкулоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;</p>\r\n            <h3>ОДБОР НА ОГРАНОКОТ ЖИТОШЕ</h3>\r\n            <p>1. Џемаил  Јусуфи&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - претседател<br>2. Иљаз  Спахич&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - секретар<br>3. Раип  Јусуфи&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp; член<br>4. Енвер  Амедоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp;&nbsp;&nbsp; &ldquo;<br>5. Џибо Љаич&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&nbsp;&nbsp;&nbsp;  &ldquo;</p>\r\n            <h3>КОМИСИЈА ЗА СПОРТ И  КУЛТУРНИ ДЕЈНОСТИ</h3>\r\n            <p>1. Ајриз  Муриќ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - претседател<br>2. Сулејман  Абази&nbsp;&nbsp;&nbsp;&nbsp; - секретар<br>3. Јусуф  Метлику&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; член<br>4. Ќани  Рустеми&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;<br>5. Ридван  Еминоски&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo;&nbsp;</p>\r\n            <p>              (Секој огранок  има своја комисија за спорт и културни дејности)</p>\r\n            <h3></h3>\r\n            <h3><br />\r\n            </h3>', '', 0, 0, 1, 0, '2024-12-31 23:00:00', '2024-12-31 23:00:00'),
(1801, 1, 1, 15, 30, 'Програма', 'programa', NULL, NULL, '<p>Согласно член 17 став  2 од Статутот на Здружението на пензионерите &ndash; Крушево, Собранието на Здружението  на седницата одржана на 12.2020 година, донесе</p>\r\n          <p align=\"center\"><strong>ПРЕДЛОГ - П  Р   О  Г  Р   А  М  А</strong><br />\r\n            <strong>За работа на Здружението на  пензионерите - Крушево за 2021 година</strong></p>\r\n          <p>Програмата за работа на Здружението на пензионерите &ndash; Крушево за 2021  година предвидува дејности и активности што произлегуваат од Статутот&nbsp; и другите акти на Здружението.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Во 2021 година активностите  ќе бидат насочени кон остварување на основните цели, особено кон заштита на  стекнатите права со што се обезбедува правна сигурност за редовно живеење,  редовна исплата на пензиите, правото на годишно зголемување на пензиите со цел  подобрување на материјалниот статус на пензионерите, остварување на поцелосна и  квалитетна здравствена заштита, како и подобрување на квалитетот на живеење со  организирање на&nbsp; спортско-рекреативни,  културно-забавни и други социјално-хуманитарни активности, во услови на пандемија од корона вирусот и од можностите кои ги диктира  пандемијата.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Програмските цели и задачи  на Здружението во 2021 година ќе се остваруваат преку:</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1.Собранието на Здружението:</strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Собранието во тековната  година ќе одржи две седници, (а по потреба и повеќе) и тоа во месец&nbsp; февруари и во месец декември.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; На седницата во февруари,  Собранието&nbsp; ќе расправа по повеќе текови  задачи од негова надлежност, посебно: Извештај за работата на органите и телата  на Здружението во 2020 година, Финансиски извештај за финансиското и  материјалното работење на Здружението во 2020 година, донесување на одлука за  усвојување на Годишната завршна сметка за 2020 година и ќе расправа за прашања  од областа на почитување на мерките за заштита од корона вирусот и за начини како  органите и телата на здружението и во услови на пандемија да продолжат со  редовна работа, се со цел пензионерите да се чувствуваат безбедно.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; На седницата во месец декември,  Собранието ќе расправа по повеќе прашања кои произлегуваат од Статутот&nbsp; и од тековното работење, а ќе ги донесе двата  најзначајни документи: Програма за работа на Здружението за 2021 и Финансиски  план на Здружението за 2021 година.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Собранието ќе расправа и  по други прашања кои произлегуваат од Статутот на Здружението и документи доставени  од СЗПМ, </p>\r\n          <p><strong>2 Извршен одбор одбор</strong>:</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Извршниот одбор во 2021  година ќе одржи планирани четири седници (по потреба и повеќе) и тоа: во месец февруари,  мај, септември и декември.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; На седниците на Извршниот  одбор ќе се расправа &nbsp;за Програмата и  финансискиот план за 2021 година, за извештаите и програмите за работа на  постојаните тела што ги формира ИО, за реализацијата на одлуките, програмите и  другите акти што ќе ги донесе Собранието, како и ќе врши и други активности согласно  Статутот на Здружението, со посебен акцент на заштита на пензионерите од корона  вирусот и обезбедување на услови и можности за работа на органите и телата на  Здружението, ограноците и нивните помошни тела.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>3 Надзорен одбор:</strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Надзорниот одбор во текот  на годината ќе се состане по потреба, а најмалку два пати и тоа во месец  јануари и јули.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Главна задача на  Надзорниот одбор е да врши контрола на финансиското и материјалното работење на  Здружението и ќе настојува тоа работење да биде во склад со постојните  позитивни закони од областа на финансиското работење.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>4 Органоците: </strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Во склопот на Здружението  функционираат четири ограноци и тоа: Огранок број 1 Крушево, огранок број 2  Крушево, огранок со седиште во с.Св.Митрани кои ги опфаќа селата: Врбоец,  Св.Митрани, Милошево, Пресил и Бучин и огранок со седиште во с.Житоше кои ги  опфаќа селата: Алданци, Норово, Селце, Јакреново, Саждево, Борино, Подвис и  Локвени,</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Во 2021 година основните  определби и насоки ќе бидат активностите за натамошно активирање на дејностите  во ограноците како основни организациони единици во кои се одвива  пензионерското организирање во Општината. </p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Секој огранок, на основа  ова Програма, ќе изготват свои програми за работа во 2021 година:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </p>\r\n          <p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5. Поквалитетна здравствена заштита  на корисниците на пензија</strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Прашањето на здравствената  заштита е постојано во фокусот на Комисијата за здравство и социјални работи на  Извршниот одбор на Здружението кој во своето работење ќе ги продолжи  активностите за подобрување на здравствената заштита на пензионерите во  Општината, посебно сега во услови на пандемија од корона вирусот. Во соработка  со Здравствениот дом&nbsp; и Општинската  организација на Црвениот крст &nbsp;ќе се преземат  активности за формирање на патронажни служби кои ќе ги посетуваат старите и  изнемоштени лица во населените места на Општината и во градот со давање на  примарна здравствена заштита (мерење на крвен притисок, шеќер во крвта и други  превентивни прегледи).</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<strong>6.  Подобрување на квалитетот на живеење на пензионерите:</strong></p>\r\n          <p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;а) Спортски  активности:</strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Спортските натпревари на пензионерите  е една од приоритетните задачи на Здружението. И во 2021 активностите кон спортот и рекреацијата ќе се одвиваат согласно  со целите на Здружението за унапредување, збогатување и проширување на  интересот на пензионерите, особено во ограноците во населените места за учество  во повеќе спортски дисциплини, онака како ќе дозволуваат условите кои ги  диктира корона кризата. Спортските игри истовремено преставуваат и еден вид на  рекреација и дружење на пензионерите од соседните здруженија и на ниво на Република.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Во 2021 година ќе се преземаат  активности преку Комисијата за спорт и рекреација за организирање на  поквалитетни тренинзи на учесниците за постигнување на подобри резултати на  ниво на Регион и на Републичките натпревари, се разбира ако тоа го дозволи  пандемијата.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<strong>б)  Организирање и изведување на еднодневна екскурзија:</strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Како и досега и во 2021  година ќе се настојува, ако тоа го дозволат условите да се организира и изведе еднодневна  екскурзија. Во 2021 година организацијата и маршутата на еднодневната  екскурзија ќе ја определи Комисијата за спорт и рекреација на ограноците, која  самостојно ќе ја организира Огранокот, а ќе се изведе&nbsp; на територијата на Републиката. Ограноците се  должни 10 дена пред изведувањето на екскурзиите, да го извести раководството на  Здружението за бројот на учесниците на екскурзијата, релацијата и времето  на&nbsp; изведувањето на еднодневната  екскурзија. Раководството е должно да обезбеди превозни средства, во зависност  од бројот на учесниците и во зависност од должината на релацијата да го  определи учеството на секој пензионер &ndash; учесник во екскурзијата. </p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>в)Културни ревии на песна, музика и&nbsp;  игри:</strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; И во 2021 година во  зависност од условите кои ги диктира корона кризата Здружението ќе земе активно  учество на регионалната&nbsp; ревија на песна,  музика и игри со пејачката и играорната група &nbsp;кои во изминатите години покажаа завидни  резултати и учествуваше на републичките ревии на песна. музика и игри.  Организацијата и активностите за успешно остварување на пејачката и играорната група  се во надлежност на Комисијата за културни дејности која ќе врши избор на песни  и игри карактеристични за нашето подрачје, се разбира ако тоа го дозволи  здравствената ситуација.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>г) Актив на пензионерките:</strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Во 2021 година Активот на  пензионерки ќе продолжи со досегашните активности, а пред се за заштита од  корона вирусот со посета на стари лица и во договор со Здравствениот дом да се  врши бесплатна патронажна служба кај изнемоштените пензионери, ќе организира  работилници за едукација и превентивни мерки за заштита од актуелни болести, а  во соработка со ОО на ЦК и Центарот за социјална заштита. Исто така, Активот ќе  организира тематски состаноци за актуелни болести, совети за спречување на  семејно насилство, предлагање на мерки за користење на редовна здравствена  заштита и други прашања.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Активот на пензионерки и  во 2021 година ќе продолжи со учество на манифестации и дружења&nbsp; организирани од други здруженија.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Горе наведените активности  како и тековни проблеми што ќе се појават во текот на годината Активот ќе ги  реализира во тесна соработка со ОО на ЦК, а согласно Спогодбата за соработка.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Во 2020 година раководството  на Активот на жените ќе преземе мерки за поголемо учество на жените во  остварување на активностите на Здружението.</p>\r\n          <p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 7 Соработка на Здружението со другите  здруженија во регионот и Локалната самоуправа:</strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Во 2021 година ќе продолжи  соработката со здруженијата од западниот регион, а особено со збратименото  Здружение од Струга, како и со соседните здруженија: Прилеп, Битола, Демир  Хисар, Ресен и други, со посебен акцент и внимание за соработката со СЗПМ и  неговите органи и тела. </p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Во 2021 година исто така  ќе продолжи досегашната соработка со Фондот на ПИОМ &ndash; Подрачна единица Крушево,  особено во исплатата на посмртната помош како и навремена евиденција за новите  и починатите пензионери што преставува услов за висината на членарината. </p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Во 2021 година ќе се преземат  активности за подобрување и натамошно продлабочување на досегашната соработка  со градоначалникот и советот на Локалната самоуправа, со присуство на претставници  од Здружението на седниците на Советот.</p>\r\n          <p><strong>&nbsp;8. Задачи на раководството и стручната  служба на Здружението:</strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; И во 2021 година раководството  и стручната служба својата активност и работа ќе ја обавува во согласност со  законите, Статутот и одлуките што ќе ги донесе Собранието и Извршниот одбор,  особено:</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - целосно, навремено и  квалитетно извршување на одлуките, заклучоците и другите документи што ќе ги  донесе Собранието и Извршниот одбор, како и спроведување на одлуките и другите  акти на СЗПМ;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - имплементација на  Одлуката за утврдување единствени критериуми за материјално-финансиско работење  на здруженијата на пензионери и СЗПМ;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - навремено и квалитетно  извршување на сите тековни задачи од секојдневното работење;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - редовно водење и следење  на финансиската состојба во Здружението;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - изработка на извештај за  финансиското работење за 2020 година;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - изработка на Финансиски  план за 2021 година;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - изработка на Извештај за  работата и активноста на Здружението за 2020 година;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - изработка на Програма за  работа на Здружението и неговите органи и тела за 2021година;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - постојана грижа за состојбата  на Пензионерскиот дом со цел да се сочува функционалноста на објектот за дневен  престој на пензионерите;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - домаќинско и строго  наменско користење на финансиските средства со кои располага Здружението;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - ќе се грижи за редовна  наплата на наемнината од деловниот простор и за кириите од становите;</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - редовно снабдување на  пензионерскиот клуб со алкохолни и безалкохолни пијалоци, огревно дрво и сл.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Собранието на здружението  и неговите органи, ќе даде целосна поддршка на сите иницијативи и барања кои ќе  ги покренат органите на СЗПМ до Државните органи, како што се: Изградба на  центри за дневен престој на пензионерите; изградба на пензионерски домови;  воведување на пензионерска социјална карта , волонтерството и сл.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Раководството на активно  ќе се вклучи во активностите кои ги спроведува СЗПМ и државните органи за  надминување на корона кризата и ќе предлага и презема мерки и активности за  заштита на здравјето на пензионерите</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Раководството на  здружението преку своите органи и тела ќе покренува и други иницијативи за  подобрување на животниот стандард на пензионерите, усогласување на пензиите со  порастот на животните трошоци и порастот на платите, вонредно зголемување на  ниските пензии, еднократни парични исплати на најзагрозените пензионери и сл.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Со конкретни иницијативи и  предлози ќе се бара остварување на правата и обврските што произлегуваат од  Спогодбите за соработка помеѓу Локалната самоуправа, како и ОО на ЦК со  Здружението на пензионерите &ndash; Крушево.</p>\r\n          <p><strong>9. Одбележување на 20-Септември- Денот на пензионерите;</strong></p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; И во 2021 година, ако тоа  го дозволува корона кризата доследно ќе биде одбележан Денот на пензионерите со  активности преку хумани акции и доделување на материјални помоши, како и  организирање на заедничко дружење на пензионерите-ките и со скромен коктел.          </p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Целосно спроведување на  Програмата &nbsp;од страна на Собранието и  неговите тела со преземање на конкретни мерки и активности за надминување и  подобрување на состојбите за успешно функционирање&nbsp; на Здружението.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ова Програма ќе биде  основа за изготвување на програми и акциони планови за работа на телата на ИО и  ограноците на Здружението.</p>\r\n          <p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Програмата е отворена за  измени и дополнувања со предлози за подобрување на истата.</p>\r\n          <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n            <tr>\r\n    <td width=\"49%\">      Крушево<br />\r\n      декември 2020 година</td>\r\n    <td width=\"51%\">Претседател  на Собрание<br />\r\n      Милорад Трпчески с.р.</td>\r\n    </tr>\r\n</table>', '', 0, 0, 1, 0, '2024-12-31 23:00:00', '2024-12-31 23:00:00'),
(1802, 1, 1, 9, 4, 'За нас', 'za-nas', NULL, NULL, '<p>Здружението е  формирано на 25.07.1998 година и на истиот ден е поднесена пријава за  запишување во Централниот регистер. Здружението има четири ограноци: Богданци,  Стојаково, Ѓавото и Селемлија. Од сите ограноци има претставници во Собранието  и во ИО на здружението. Собранието има 21 член, а ИО има 9 члена. Во составот  на ОЗП-Богданци има Актив на пензионерки.<br />\r\n            Од областа на културата здружението учествува редовно на Регионалните ревии на  песни, музика и игра, како и на други фолклорни манифестации. Пензионерите  редовно се присутни на пикниците на зелено низ цела држава. Согледно програмата  секоја година се организираат екскурзии и тоа: пролетна и есенска со  партиципација од здружението. Во рамките на културно забавниот живот се  организираат посети на театарски претстави во кои учествуваат пензионери,  членови на здружението и друго. <br />\r\n            Хуманоста кон најзагрозените е присутна преку доделување на парична помош и  пакети со основни артикли во вредност според можностите на здружението.<br />\r\n            Општинското здружение на пензионери од Богданци редовно учествува на  регионалните и републичките пензионерски спортски активности согласно  програмата на СЗПМ. <br />\r\n            Претседател на ОЗП-Богданци е Васил Туранов. Здружението нема свој пензионерски  клуб. Канцеларијата и просторијата за активности се во сопственост на Фондот на  ПИОМ.</p>', '', 0, 0, 1, 0, '2024-12-31 23:00:00', '2024-12-31 23:00:00'),
(1803, 1, 1, 25, 4, 'Контакт', 'kontakt', NULL, NULL, '<h2>ОПШТИНСКО ЗДРУЖЕНИЕ НА ПЕНЗИОНЕРИ - БОГДАНЦИ</h2>\r\n          <p>ул. Маршал Тито  бр.111 БОГДАНЦИ<br />\r\n            e-mail: <a href=\"mailto:ozp.bogdanci@gmail.com\">ozp.bogdanci@gmail.com</a></p>\r\n          <p>тел. 034 221 113</p>', '', 0, 0, 1, 0, '2024-12-31 23:00:00', '2024-12-31 23:00:00'),
(1804, 1, 1, 10, 4, 'Организација', 'organizacija', NULL, NULL, '<table class=\"dijagram\" width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"3\">Собрание</td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"33%\">Извршен одбор</td>\r\n			<td width=\"33%\">Надзорен одбор</td>\r\n			<td width=\"33%\">Статутарна комисија</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table class=\"dijagram\" width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td width=\"50%\">Комисија за спорт и рекреација</td>\r\n			<td>Комисија за културно забавен живот</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Комисија за здравство и социјални грижи</td>\r\n			<td>Претседатели на ограноци</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p align=\"center\">&nbsp;</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" class=\"bez_padding\" width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td align=\"center\" width=\"33%\"><img alt=\"\" height=\"160\" src=\"/vesnik/images/zdruzenija/bogdanci/Aleksandar-Turkas-pretsedatel-na-Sobranie.jpg\" width=\"110\" /></td>\r\n			<td align=\"center\" width=\"1%\">&nbsp;</td>\r\n			<td align=\"center\" width=\"33%\"><img alt=\"\" height=\"160\" src=\"/vesnik/images/zdruzenija/bogdanci/Vasil-Turanov-pretsedatel-na-ZP-Bogdanci.jpg\" width=\"110\" /></td>\r\n			<td align=\"center\" width=\"1%\">&nbsp;</td>\r\n			<td align=\"center\" width=\"33%\">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\" valign=\"top\">Александар Туркаш - претседавач на собрание</td>\r\n			<td align=\"center\" valign=\"top\">&nbsp;</td>\r\n			<td align=\"center\" valign=\"top\">Васил Туранов - претседател на ЗП и на ИО</td>\r\n			<td align=\"center\" valign=\"top\">&nbsp;</td>\r\n			<td align=\"center\" valign=\"top\">&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2>Органи и тела на ЗП Богданци - 2023 &ndash; 2027 год.</h2>\r\n\r\n<h2>Претседавач на Собрание на ЗП</h2>\r\n\r\n<p>Александар Туркаш</p>\r\n\r\n<h2>СОБРАНИЕ</h2>\r\n\r\n<ol>\r\n	<li>Васил Туранов</li>\r\n	<li>Јован Беј